INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (29139, 'Veratens 40mg 10 f.c. tab.', 'فيراتنس 40مجم 10 اقراص', '4', NULL, 'Antihypertensive.cardioselective calcium chennel blocker', 'Verapamil', 'Pharopharma', 'Tab', 'أقراص', '40mg', '1', NULL, 'about verapamil calcium channel blocker phenylalkylamine derivative class iv antiarrhythmic antihypertensive. mechanism of action of verapamil verapamil is a phenylalkylamine; ca channel blocker. it exerts it`s antianginal antihypertensive antiarrythmic a', NULL, NULL, 709, '2023-05-19', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Verapamil is a phenylalkylamine calcium channel blocker used in the treatment of high blood pressure, heart arrhythmias, and angina,[L8791] and was the first calcium channel antagonist to be introduced into therapy in the early 1960s.[A188514] It is a member of the non-dihydropyridine class of calcium channel blockers, which includes drugs like [diltiazem] and [flunarizine], but is chemically unrelated to other cardioactive medications.[L8791] Verapamil is administered as a racemic mixture containing equal amounts of the S- and R-enantiomer, each of which is pharmacologically distinct - the S-enantiomer carries approximately 20-fold greater potency than the R-enantiomer, but is metabolized at a higher rate.[A188435]', '["C09BB10", "C08DA51", "C08DA01"]', '[]');
