INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (29354, 'Vilzolid 600mg 10 f.c. tablets', 'فيلزوليد 600مجم 10 اقراص', '142', '110', 'Antibiotic.oxazolidinone', 'Linezolid', 'Delta grand pharma > parkville pharmaceuticals', 'Tablet', 'أقراص', '600mg', '1', 'مضاد حيوي', NULL, '6224008073062', NULL, 4655, '2024-08-26', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Linezolid is a synthetic antibiotic which is used for the treatment of infections caused by aerobic Gram-positive bacteria. Its effects are bacteriostatic against both enterococci and staphylococci and bactericidal against most isolates of streptococci.[L32965] Linezolid exerts its antibacterial activity by inhibiting the initiation of bacterial protein synthesis - more specifically, it binds to the 23S ribosomal RNA of the 50S subunit[A11227,A199050] and, in doing so, prevents the formation of the 70S initiation complex which is essential for bacterial reproduction.

Linezolid was initially approved in 2000 and was the first member of the oxazolidinone antibiotic class.[A233425] A second member of this class, [tedizolid], was approved by the FDA in 2014 and is considered generally more effective and tolerable than its predecessor.', '["J01XX08"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (29355, 'Vimodame 2% vaginal cream 40 gm', 'فيمودام 2% كريم 40جم', '37', NULL, 'Antibiotic.lincomycins', 'Clindamycin', 'Egpi > pharma 3 company', 'Cream', 'كريم', '2%', '1', NULL, 'mechanism clindamycin works by preventing bacteria from producing proteins that are essential to them. without these proteins the bacteria cannot grow replicate and increase in numbers. the remaining bacteria eventually die or are destroyed by the immune', NULL, NULL, 911, '2022-07-13', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Clindamycin is a semi-synthetic lincosamide antibiotic used in the treatment of a variety of serious infections due to susceptible microorganisms[L11599,L11596] as well as topically for acne vulgaris.[L11593] It has a relatively narrow spectrum of activity that includes anaerobic bacteria as well as gram-positive cocci and bacilli and gram-negative bacilli.[A190621] Interestingly, clindamycin appears to carry some activity against protozoans, and has been used off-label in the treatment of toxoplasmosis, malaria, and babesiosis.[A190657]

Clindamycin is derived from, and has largely replaced, [lincomycin], a naturally occurring lincosamide and the eponymous member of this antibiotic class, due to its improved properties over the parent compound. The name lincomycin is derived from Lincoln, Nebraska, where it was first isolated from _Streptomyces lincolnensis_ found in a soil sample.[A190621]', '["D10AF01", "J01FF01", "D10AF51", "G01AA10"]', '[]');
