INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (29596, 'Vortiocyrl 10 mg 14 f.c. tabs.', 'فورتيوسيرل 10مجم 14 قرص', '104', NULL, 'Psychiatric.antidepressants', 'Vortioxetine', 'Marcyrl co.', 'Tab', 'أقراص', '10 mg', '2', 'مضاد إكتئاب', 'vortioxetine is an atypical antidepressant (a serotonin modulator and stimulator). vortioxetine is used as a treatment for major depressive disorder.', NULL, NULL, 1646, '2022-05-12', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Vortioxetine is an antidepressant medication indicated for the treatment of major depressive disorder (MDD). It is classified as a serotonin modulator and stimulator (SMS) as it has a multimodal mechanism of action towards the serotonin neurotransmitter system whereby it simultaneously modulates one or more serotonin receptors and inhibits the reuptake of serotonin. More specifically, vortioxetine acts via the following biological mechanisms: as a serotonin reuptake inhibitor (SRI) through inhibition of the serotonin transporter, as a partial agonist of the 5-HT1B receptor, an agonist of 5-HT1A, and an antagonist of the 5-HT3, 5-HT1D, and 5-HT7 receptors. SMSs were developed because there are many different subtypes of serotonin receptors, however, not all of these receptors appear to be involved in the antidepressant effects of SRIs. Some serotonin receptors seem to play a relatively neutral or insignificant role in the regulation of mood, but others, such as 5-HT1A autoreceptors and 5-HT7 receptors, appear to play an oppositional role in the efficacy of SRIs in treating depression.', '["N06AX26"]', '[]');
