INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18834, 'Farmactive silver spray 125 ml', 'فارماكتيف سيلفر سبراي 125 مل', '699', '499', 'Healing topical', 'Silver colloid+hyaluronic acid sodic salt', 'Farmac-zabban > elegant care group', 'Spray', 'بخاخ', '125 ml', '1', 'اسبراي يستخدم للجروح و القرح -- تقليل الالتهابات -- تقليل الاحمرار و الحكة', 'topical spray for burns and ulcers', '8000246002952', NULL, 8240, '2024-02-15', 0, 'Indicated as an adjunct for the prevention and treatment of wound sepsis in patients with second- and third-degree burns.', 'Studies utilizing radioactive micronized silver sulfadiazine, electron microscopy, and biochemical techniques have revealed that the mechanism of action of silver sulfadiazine on bacteria differs from silver nitrate and sodium sulfadiazine. Silver sulfadiazine acts only on the cell membrane and cell wall to produce its bactericidal effect. A specific mechanism of action has not been determined, but silver sulfadiazine''s effectiveness may possibly be from a synergistic interaction, or the action of each component. Silver is a biocide, which binds to a broad range of targets. Silver ions bind to nucleophilic amino acids, as well as sulfhydryl, amino, imidazole, phosphate, and carboxyl groups in proteins, causing protein denaturation and enzyme inhibition. 

Silver binds to surface membranes and proteins, causing proton leaks in the membrane, leading to cell death. 

Sulfadiazine is a competitive inhibitor of bacterial para-aminobenzoic acid (PABA), a substrate of the enzyme dihydropteroate synthetase. The inhibited reaction is necessary in these organisms for the synthesis of folic acid.', 'Silver sulfadiazine has broad antimicrobial activity. It is bactericidal for many gram- negative and gram-positive bacteria as well as being effective against yeast. Silver sulfadiazine is not a carbonic anhydrase inhibitor and may be useful in situations where such agents are contraindicated.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18836, 'Farmorubicin rd 50mg vial', 'فارموروبيسين ار دي 50 مجم فيال', '612', NULL, 'Antineoplastic', 'Epirubicin', 'Pfizer', 'Vial', 'فيال', '50mg', '1', NULL, 'about epirubicin anthracycline anibiotic antineoplastic. mechanism of action of epirubicin this anthracycline drug intercalates dna strands which results complex formation it will inhibit dna & rna synthesis. it triggers dna cleavage by inhibiting topoiso', NULL, NULL, 1122, '2022-07-17', 0, 'For use as a component of adjuvant therapy in patients with evidence of axillary node tumor involvement following resection of primary breast cancer.', 'Epirubicin has antimitotic and cytotoxic activity. It inhibits nucleic acid (DNA and RNA) and protein synthesis through a number of proposed mechanisms of action: Epirubicin forms complexes with DNA by intercalation between base pairs, and it inhibits topoisomerase II activity by stabilizing the DNA-topoisomerase II complex, preventing the religation portion of the ligation-religation reaction that topoisomerase II catalyzes. It also interferes with DNA replication and transcription by inhibiting DNA helicase activity.', 'Epirubicin is an antineoplastic in the anthracycline class. General properties of drugs in this class include: interaction with DNA in a variety of different ways including intercalation (squeezing between the base pairs), DNA strand breakage and inhibition with the enzyme topoisomerase II. Most of these compounds have been isolated from natural sources and antibiotics. However, they lack the specificity of the antimicrobial antibiotics and thus produce significant toxicity. The anthracyclines are among the most important antitumor drugs available. Doxorubicin is widely used for the treatment of several solid tumors while daunorubicin and idarubicin are used exclusively for the treatment of leukemia. Epirubicin may also inhibit polymerase activity, affect regulation of gene expression, and produce free radical damage to DNA. Epirubicin possesses an antitumor effect against a wide spectrum of tumors, either grafted or spontaneous. The anthracyclines are cell cycle-nonspecific.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18838, 'Farostan forte 500mg 10 tab.', 'فاروستان فورت 500 مجم 10 قرص', '5', NULL, 'Nsaid.fenamates', 'Mefenamic acid', 'Pharopharma', 'Tab', 'أقراص', '500mg', '1', NULL, 'about mefenamic acid nsaid analgesic antiinflammatory. mechanism of action of mefenamic acid mefenamic acid has analgesic anti-inflammatory and antipyretic action. it acts by inhibiting prostaglandin (pgs) synthesis and their release at the site of injury', NULL, NULL, 1274, '2022-07-28', 0, 'For the treatment of rheumatoid arthritis, osteoarthritis, dysmenorrhea, and mild to moderate pain, inflammation, and fever.', 'Mefenamic acid binds the prostaglandin synthetase receptors COX-1 and COX-2, inhibiting the action of prostaglandin synthetase. As these receptors have a role as a major mediator of inflammation and/or a role for prostanoid signaling in activity-dependent plasticity, the symptoms of pain are temporarily reduced.', 'Mefenamic acid, an anthranilic acid derivative, is a member of the fenamate group of nonsteroidal anti-inflammatory drugs (NSAIDs). It exhibits anti-inflammatory, analgesic, and antipyretic activities. Similar to other NSAIDs, mefenamic acid inhibits prostaglandin synthetase.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18842, 'Faslodex 250mg/5ml i.m. 2 prefilled syringe', 'فاسلودكس 250 مجم / 5 مل 2 حقن مملؤة مسبقاً', '10,330', '7600', 'Antineoplastic', 'Fulvestrant', 'Astra zeneca', 'Syringe', 'حقنة', '250mg', '2', NULL, NULL, NULL, NULL, 1727, '2025-08-12', 0, 'For the treatment of hormone receptor positive metastatic breast cancer in postmenopausal women with disease progression following anti-estrogen therapy, as monotherapy or in combination with other antineoplastic agents.', 'Fulvestrant competitively and reversibly binds to estrogen receptors present in cancer cells and achieves its anti-estrogen effects through two separate mechanisms. First, fulvestrant binds to the receptors and downregulates them so that estrogen is no longer able to bind to these receptors. Second, fulvestrant degrades the estrogen receptors to which it is bound. Both of these mechanisms inhibit the growth of tamoxifen-resistant as well as estrogen-sensitive human breast cancer cell lines.', 'Fulvestrant for intramuscular administration is an estrogen receptor antagonist without known agonist effects.', 'DrugBank', 0, 0, 0);
