INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18946, 'Fenolip 100 mg 10 caps.', 'فينوليب 100 مجم 10 كبسولات', '6', NULL, 'Antihyperlipidemic.fibrates', 'Fenofibrate', 'Alfacure pharmaceuticals', 'Cap', 'كبسولة', '100 mg', '1', NULL, 'about fenofibrate fibric acid derivative ppara agonist antilipemic agent antigout. mechanism of action of fenofibrate it is prodrug which has greater ldl (low density lipoprotein) cholesterol lowering potential. it lowers circulating triglyceride levels b', NULL, NULL, 824, '2022-07-12', 0, 'Fenofibrate is indicated as adjunctive therapy to diet:[L51464]



- To reduce triglyceride (TG) levels in adults with severe hypertriglyceridemia (TG greater than or equal to 500 mg/dL)

- To reduce elevated low-density lipoprotein cholesterol (LDL-C) in adults with primary hyperlipidemia when the use of recommended LDL-C lowering therapy is not possible', 'Fenofibrate activates peroxisome proliferator activated receptor alpha (PPARα), increasing lipolysis, activating lipoprotein lipase, and reducing apoprotein C-III.[A186002,L8588,L8591] PPARα is a nuclear receptor and its activation alters lipid, glucose, and amino acid homeostasis.[A186002] Activation of PPARα activates transcription of gene transcription and translation that generates peroxisomes filled with hydrogen peroxide, reactive oxygen species, and hydroxyl radicals that also participate in lipolysis.[A186005] This mechanism of increased lipid metabolism is also associated with increased oxidative stress on the liver.[A186005] In rare cases this stress can lead to cirrhosis and chronic active hepatitis.[A186002,L8588,L8591]', 'Fenofibrate is a fibrate that activates peroxisome proliferator activated receptor alpha (PPARα) to alter lipid metabolism and treat primary hypercholesterolemia, mixed dyslipidemia, and severe hypertriglyceridemia.[A186002,L8588,L8591] Fenofibrate requires once daily dosing and has a half life of 19-27 hours so its duration of action is long.[A36366,L8588,L8591] Fenofibrate capsules are given at a dose of 50-150mg daily so the therapeutic index is wide.[L8591] Patients should be counselled about the risk of rhabdomyolysis, myopathy, and cholelithiasis when taking fibrates.[L8588,L8591]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18948, 'Fenoprofen 300mg 30 caps.', 'فينوبروفين 300مجم 30 كبسولة', '27', NULL, 'Nsaid.propionic acid derivatives', 'Fenoprofen', 'Western pharmaceuticals industries', 'Cap', 'كبسولة', '300mg', '3', NULL, 'description an anti-inflammatory analgesic and antipyretic highly bound to plasma proteins. it is pharmacologically similar to aspirin but causes less gastrointestinal bleeding. indication for relief of the signs and symptoms of rheumatoid arthritis and o', '6224007308110', NULL, 892, '2022-12-05', 0, 'For relief of the signs and symptoms of rheumatoid arthritis and osteoarthritis. Also for the relief of mild to moderate pain.', 'Fenoprofen''s exact mode of action is unknown, but it is thought that prostaglandin synthetase inhibition is involved. Fenoprofen has been shown to inhibit prostaglandin synthetase isolated from bovine seminal vesicles.', 'Fenoprofen is a propionic acid derivative with analgesic, antiinflammatory and antipyretic properties. Fenoprofen inhibits prostaglandin synthesis by decreasing the enzyme needed for biosynthesis. In patients with rheumatoid arthritis, the anti-inflammatory action of fenoprofen has been evidenced by relief of pain, increase in grip strength, and reductions in joint swelling, duration of morning stiffness, and disease activity (as assessed by both the investigator and the patient). In patients with osteoarthritis, the anti-inflammatory and analgesic effects of fenoprofen have been demonstrated by reduction in tenderness as a response to pressure and reductions in night pain, stiffness, swelling, and overall disease activity (as assessed by both the patient and the investigator). These effects have also been demonstrated by relief of pain with motion and at rest and increased range of motion in involved joints. In patients with rheumatoid arthritis and osteoarthritis, clinical studies have shown fenoprofen to be comparable to aspirin in controlling the aforementioned measures of disease activity, but mild gastrointestinal reactions (nausea, dyspepsia) and tinnitus occurred less frequently in patients treated with fenoprofen than in aspirin-treated patients. It is not known whether fenoprofen causes less peptic ulceration than does aspirin. In patients with pain, the analgesic action of fenoprofen has produced a reduction in pain intensity, an increase in pain relief, improvement in total analgesia scores, and a sustained analgesic effect.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18949, 'Cholcarol 200 i.u/drop oral drops 15 ml', 'شولكارول 200 وحدة دولية/نقطة نقط 15 مل', '35', NULL, 'Vitamin d3', 'Cholecalciferol (vitamin d3 ) 200 iu', 'Acs', 'Drops', 'نقط', '15 ml', '1', NULL, NULL, NULL, NULL, 1127, '2022-08-15', 0, 'Cholecalciferol use is indicated for the treatment of specific medical conditions like refractory rickets (or vitamin D resistant rickets), hypoparathyroidism, and familial hypophosphatemia [F4027, F4042].



Concurrently, as one of the most commonly utilized forms of vitamin D, cholecalciferol is also very frequently used as a supplement in individuals to maintain sufficient vitamin d levels in the body or to treat vitamin D deficiency, as well as various medical conditions that can be associated directly or indirectly with vitamin d insufficiency like osteoporosis and chronic kidney disease, among others [A176041, A176044, F4051].', 'Most individuals naturally generate adequate amounts of vitamin D through ordinary dietary intake of vitamin D (in some foods like eggs, fish, and cheese) and natural photochemical conversion of the vitamin D3 precursor 7-dehydrocholesterol in the skin via exposure to sunlight [L5689, F4027, F4042, F4048].



Conversely, vitamin D deficiency can often occur from a combination of insufficient exposure to sunlight, inadequate dietary intake of vitamin D, genetic defects with endogenous vitamin D receptor, or even severe liver or kidney disease [A243]. Such deficiency is known for resulting in conditions like rickets or osteomalacia, all of which reflect inadequate mineralization of bone, enhanced compensatory skeletal demineralization, resultant decreased calcium ion blood concentrations, and increases in the production and secretion of parathyroid hormone [A223]. Increases in parathyroid hormone stimulate the mobilization of skeletal calcium and the renal excretion of phosphorus [A223]. This enhanced mobilization of skeletal calcium leads towards porotic bone conditions [A223].



Ordinarily, while vitamin D3 is made naturally via photochemical processes in the skin, both itself and vitamin D2 can be found in various food and pharmaceutical sources as dietary supplements. The principal biological function of vitamin D is the maintenance of normal levels of serum calcium and phosphorus in the bloodstream by enhancing the efficacy of the small intestine to absorb these minerals from the diet [A223]. At the liver, vitamin D3 or D2 is hydroxylated to 25-hydroxyvitamin D and then finally to the primary active metabolite 1,25-dihydroxyvitamin D in the kidney via further hydroxylation [A223, A243]. This final metabolite binds to endogenous vitamin d receptors, which results in a variety of regulatory roles - including maintaining  calcium balance, the regulation of parathyroid hormone, the promotion of the renal reabsorption of calcium, increased intestinal absorption of calcium and phosphorus, and increased calcium and phosphorus mobilization of calcium and phosphorus from bone to plasma to maintain balanced levels of each in bone and the plasma [A223, A243].



In particular, calcitriol interacts with vitamin D receptors in the small intestine to enhance the efficiency of intestinal calcium and phosphorous absorption from about 10-15% to 30-40% and 60% increased to 80%, respectively [L5689]. Furthermore, calcitriol binds with vitamin D receptors in osteoblasts to stimulate a receptor activator of nuclear factor kB ligand (or RANKL) which subsequently interacts with receptor activator of nuclear factor kB (NFkB) on immature preosteoclasts, causing them to become mature bone-resorbing osteoclasts [L5689]. Such mature osteoclasts ultimately function in removing calcium and phosphorus from bone to maintain blood calcium and phosphorus levels [L5689]. Moreover, calcitriol also stimulates calcium reabsorption from the glomerular filtrate in the kidneys [L5689].



Additionally, it is believed that when calcitriol binds with nuclear vitamin D receptors, that this bound complex itself binds to retinoic acid X receptor (RXR) to generate a heterodimeric complex that consequently binds to specific nucleotide sequences in the DNA called vitamin D response elements [L5689]. When bound, various transcription factors attach to this complex, resulting in either up or down-regulation of the associated gene''s activity. It is thought that there may be as much as 200 to 2000 genes that possess vitamin D response elements or that are influenced indirectly to control a multitude of genes across the genome [L5689].  It is in this way that cholecalciferol is believed to function in regulating gene transcription associated with cancer risk, autoimmune disorders, and cardiovascular disease linked to vitamin D deficiency [L5689]. In fact, there has been some research to suggest calcitriol may also be able to prevent malignancies by inducing cellular maturation and inducing apoptosis and inhibiting angiogenesis, exhibit anti-inflammatory effects by inhibiting foam cell formation and promoting angiogenesis in endothelial colony-forming cells in vitro, inhibit immune reactions by enhancing the transcription of endogenous antibiotics like cathelicidin and regulate the activity and differentiation of CD4+ T cells, amongst a variety of other proposed actions [L5689].   ', 'The in vivo synthesis of the predominant two biologically active metabolites of vitamin D occurs in two steps. The first hydroxylation of vitamin D3 cholecalciferol (or D2) occurs in the liver to yield 25-hydroxyvitamin D while the second hydroxylation happens in the kidneys to give 1, 25-dihydroxyvitamin D [F4027, F4042, F4048]. These vitamin D metabolites subsequently facilitate the active absorption of calcium and phosphorus in the small intestine, serving to increase serum calcium and phosphate levels sufficiently to allow bone mineralization [F4027, F4042, F4048]. Conversely, these vitamin D metabolites also assist in mobilizing calcium and phosphate from bone and likely increase the reabsorption of calcium and perhaps also of phosphate via the renal tubules [F4027, F4042, F4048]. There exists a period of 10 to 24 hours between the administration of cholecalciferol and the initiation of its action in the body due to the necessity of synthesis of the active vitamin D metabolites in the liver and kidneys [F4027, F4042, F4048]. It is parathyroid hormone that is responsible for the regulation of such metabolism at the level of the kidneys [F4027, F4042, F4048].', 'DrugBank', 0, 0, 0);
