INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19050, 'Despruderm 0.05% topical lotion 60 ml', 'ديسبروديرم لوشن 60 مل', '66', '46', 'Glucocorticoid', 'Desonide', 'Egpi > glenmark pharmaceuticals', 'Lotion', 'لوشن', '0.05%', '1', NULL, NULL, NULL, NULL, 810, '2025-11-03', 0, 'For the relief of the inflammatory and pruritic manifestations of corticosteroid responsive dermatose.', 'Like other topical corticosteroids, desonide has anti-inflammatory, antipruritic and vasoconstrictive properties. The drug binds to cytosolic glucocorticoid receptors. This complex migrates to the nucleus and binds to genetic elements on the DNA. This activates and represses various genes. However corticosteroids are thought to act by the induction of phospholipase A2 inhibitory proteins, collectively called lipocortins. It is postulated that these proteins control the biosynthesis of potent mediators of inflammation such as prostaglandins and leukotrienes by inhibiting the release of their common precursor arachidonic acid. Arachidonic acid is released from membrane phospholipids by phospholipase A2.', 'Desonide is a synthetic nonfluorinated corticosteroid for topical dermatologic use. The corticosteroids constitute a class of primarily synthetic steroids used topically as anti-inflammatory and antipruritic agents.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19051, 'Finshape 120mg 30 caps.', 'فينشيب 120مجم 30 كبسولة', '240', '216', 'Weight loss', 'Orlistat', 'Mash premiere', 'Cap', 'كبسولة', '120mg', '3', NULL, 'about orlistat anti-obese drug. saturated derivative of lipstatin. mechanism of action of orlistat its primary function is preventing the absorption of fats from the human diet thereby reducing caloric intake.orlistat works by inhibiting gastric and pancr', '6222001402209', NULL, 1836, '2025-12-12', 0, 'Orlistat is indicated for obesity management including weight loss and weight maintenance when used in combination with calorie reduction in overweight and obese adults; this indication applies to both the prescription formulation of 120 mg[L11130] and the over-the-counter formulation of 60 mg.[L31963] Orlistat in the 120 mg prescription formulation is also indicated to reduce the risk of weight regain following weight loss.[L11130]', 'Orlistat is a potent and selective inhibitor of various lipase enzymes responsible for the metabolism of fat. It acts in the gastrointestinal (GI) tract via covalent binding to the serine residues located on the active site of both gastric and pancreatic lipase. When orlistat is taken with food containing fat, it partially inhibits the hydrolysis of triglycerides. This decreases absorption of monoaclglycerides and free fatty acids, contributing to weight maintenance and weight loss.[A37653,L11130]', 'Orlistat helps with weight reduction and maintenance by inhibiting the absorption of dietary fats via the inhibition of lipase enzymes.[A1734,A1735]', 'DrugBank', 0, 0, 0);
