INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25555, 'Quinolorox 500mg 10 f.c. tab.', 'كيونولوركس 500 مجم 10 أقراص', '10', NULL, 'Antibiotic.quinolone', 'Ciprofloxacin', 'Epci > roxxen', 'Tab', 'أقراص', '500mg', '1', NULL, 'about ciprofloxacin it is a broad spectrum fluoroquinolone with activity against pseudomonas aeruginosa. indications for ciprofloxacin 1.urinary tract infection 2.respiratory tract infection 3.bone and joint infections 4.infectious diarrhoea 5.gonorrhoea', NULL, NULL, 791, '2022-07-12', 'Ciprofloxacin is only indicated in infections caused by susceptible bacteria.[L6469,L6472,L6475,L6478,L6481,L6484,L6487,L6490,L6493]



Ciprofloxacin immediate release tablets, oral suspensions, and intravenous injections are indicated for the treatment of skin and skin structure infections, bone and joint infections, complicated intra-abdominal infections, nosocomial pneumonia, febrile neutropenia, adults who have inhaled anthrax, plague, chronic bacterial prostatitis, lower respiratory tract infections including acute exacerbations of chronic bronchitis, urinary tract infections, complicated urinary tract infections in pediatrics, complicated pyelonephritis in pediatrics, and acute sinusitis.[L6481,L6478]



A ciprofloxacin otic solution and otic suspension with hydrocortisone are indicated for acute otitis externa.[L6469,L6484] Ciprofloxacin suspension with dexamethasone is indicated for acute otitis media in pediatric patients with tympanostomy tubes or acute otitis externa.[L6490] A ciprofloxacin intratympanic injection is indicated for pediatric patients with bilateral otitis media with effusion who are having tympanostomy tubes placed or pediatric patients 6 months or older with acute otitis externa.[L6493]



A ciprofloxacin eye drop is indicated for bacterial corneal ulcers and conjunctivitis.[L6472] A ciprofloxacin eye ointment is indicated for bacterial conjunctivitis.[L6475]



A ciprofloxacin extended release tablet is indicated for uncomplicated urinary tract infections, complicated urinary tract infections, and acute uncomplicated pyelonephritis.[L6487]', 'Ciprofloxacin acts on bacterial topoisomerase II (DNA gyrase) and topoisomerase IV.[A178885] Ciprofloxacin''s targeting of the alpha subunits of DNA gyrase prevents it from supercoiling the bacterial DNA which prevents DNA replication.[A178876,A178882]', 'Ciprofloxacin is a second generation fluoroquinolone that is active against many Gram negative and Gram positive bacteria.[A178870,L6469,L6472,L6475,L6478,L6481,L6484,L6487,L6490,L6493] It produces its action through inhibition of bacterial DNA gyrase and topoisomerase IV.[A178885] Ciprofloxacin binds to bacterial DNA gyrase with 100 times the affinity of mammalian DNA gyrase.[A178894] There is no cross resistance between fluoroquinolones and other classes of antibiotics, so it may be of clinical value when other antibiotics are no longer effective.[A178894] Ciprofloxain and its derivatives are also being investigated for its action against malaria, cancers, and AIDS.[A178885]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (5227, 'Dapsocyrl 5 % topical gel 30 gm', 'دابسوكيرل 5% جل 30 جم', '119', '65', 'Anti-acne', 'Dapsone', 'Marcyrl pharmaceutical industries (mpi)', 'Gel', 'جل', '5 %', '1', NULL, NULL, '6223003570958', NULL, 5009, '2024-10-01', 'For the treatment and management of leprosy and dermatitis herpetiformis.', 'Dapsone acts against bacteria and protozoa in the same way as sulphonamides, that is by inhibiting the synthesis of dihydrofolic acid through competition with para-amino-benzoate for the active site of dihydropteroate synthetase. The anti-inflammatory action of the drug is unrelated to its antibacterial action and is still not fully understood.', 'Dapsone is a sulfone with anti-inflammatory immunosuppressive properties as well as antibacterial and antibiotic properties. Dapsone is the principal drug in a multidrug regimen recommended by the World Health Organization for the treatment of leprosy. As an anti-infective agent, it is also used for treating malaria and, recently, for Pneumocystic carinii pneumonia in AIDS patients. Dapsone is absorbed rapidly and nearly completely from the gastrointestinal tract. Dapsone is distributed throughout total body water and is present in all tissues. However, it tends to be retained in skin and muscle and especially in the liver and kidney: traces of the drug are present in these organs up to 3 weeks after therapy cessation.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (9664, 'Pavulon 4mg/2 ml 50 amp', 'بافلون 4مجم/2مل 50 امبولة', '400', NULL, 'Nondepolarizing neuromuscular blocker', 'Pancuronium bromide', 'Organon', 'Amp', 'أمبول', '4mg', '50', 'استرخاء العضلات -- مخدر عام للجسم -- مضاد للحساسية', NULL, NULL, NULL, 1223, '2023-02-06', 'Used as a muscle relaxant during anesthesia and surgical procedures.', 'Nondepolarizing neuromuscular blocking agents inhibit neuromuscular transmission by competing with acetylcholine for the cholinergic receptors of the motor end plate, thereby reducing the response of the end plate to acetylcholine. This type of neuromuscular block is usually antagonized by anticholinesterase agents.', 'Pancuronium is a non-depolarising muscle relaxant similar to curare. It acts as a competitive acetylcholine antagonist on neuromuscular junctions, displacing acetylcholine (hence competitive) from its post-synaptic nicotinic acetylcholine receptors. It is, unlike suxamethonium, a non-depolarising agent, which means, that it causes no spontaneous depolarisations upon association with the nicotinic receptor in neuromuscular junction, thus producing no muscle fasciculations upon administration. Pancuronium has no hormonal activity. It exerts slight vagolytic activity (i.e. diminishing activity of the vagus nerve) and no ganglioplegic (i.e., blocking ganglions) activity.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13260, 'Aape renewal sunscreen spf 45 - 60 ml', 'ايبي رينيوال صن سكرين اس بي اف 45 - 60 مل', '225', NULL, 'Sun block', NULL, 'Prostemics > normandy pharma', 'Unknown', 'غير محدد', '60 ml', '1', NULL, 'aape an abbreviation for advanced adipose-derived stem cells protein extracts is a mixture of refined growth factor proteins that is extracted from human adipose-derived stem cells conditioned media. with its unique protein formula aape maximizes the revi', NULL, NULL, 669, '2022-08-16', NULL, NULL, NULL, NULL, 0, 0, 0);
