INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24796, 'Pharmaceris e-emotopic special lipid-replenishing cream 75 ml', 'فارماسيرز اي كريم 75مل', '140', NULL, 'Skin care', NULL, 'Laboratorium kosmetyczne dr irena eris sp. z o.o. > multitrade', 'Cream', 'كريم', '75 ml', '1', NULL, 'indications recommended for very dry sensitive and atopic skin prone to irritation microdamage and redness as well as itching and burning. recommended for people with atopic dermatitis regardless of age. for conditioning use in people during and after der', NULL, NULL, 674, '2022-08-02', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (6484, 'Perfection eye contour cream 30g', 'بيرفكشن كريم لمحيط العين 30 جم', '150', NULL, 'Skin care.eye contour', 'Vitamin c+vitamin e+liquorice+hydrolyzed collagen+hydrolyzed elastine+and ecoslim caffeine', 'Perfection pharmaceuticals', 'Cream', 'كريم', NULL, '1', NULL, NULL, NULL, NULL, 764, '2022-08-03', 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (5626, 'Glucose 20% (otsuka) i.v. inf. 500 ml (rubber cap)', 'جلوكوز 20% اوتوسوكا محلول وريدي 500 مل زجاجة بغطاء مطاطي', '11', NULL, 'Sterile solution', 'Glucose (dextrose)', 'Otsuka', 'Cap', 'كبسولة', '20%', '1', NULL, NULL, NULL, NULL, 1081, '2022-12-05', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26673, 'Sedacoron 200mg 20 tab.', 'سيداكورون 200مجم 20 اقراص', '11', NULL, 'Antiarrhythmias.class iii', 'Amiodarone', 'Sandoz > b.p pharma', 'Tab', 'أقراص', '200mg', '2', NULL, 'description an antianginal and antiarrhythmic drug. it increases the duration of ventricular and atrial muscle action by inhibiting na k-activated myocardial adenosine triphosphatase. there is a resulting decrease in heart rate and in vascular resistance.', NULL, NULL, 1166, '2022-07-12', 'The FDA approved indications for amiodarone are recurrent ventricular fibrillation (VF) and recurrent hemodynamically unstable ventricular tachycardia (VT). The FDA emphasizes that this drug should only be given in these conditions when they are clinically documented and have not responded to normal therapeutic doses of other antiarrhythmic agents, or when other drugs are not tolerated by the patient.[L3561]



Off-label indications include atrial fibrillation and supraventricular tachycardia.[A189666,A189720,A189723,L11286]', 'Amiodarone is considered a class III anti-arrhythmic drug. It blocks potassium currents that cause repolarization of the heart muscle during the third phase of the cardiac action potential. As a result amiodarone increases the duration of the action potential as well as the effective refractory period for cardiac cells (myocytes). Therefore, cardiac muscle cell excitability is reduced, preventing and treating abnormal heart rhythms.[A183137,A189747]



Unique from other members of the class III anti-arrhythmic drug class, amiodarone also interferes with the functioning of beta-adrenergic receptors, sodium channels, and calcium channels channels. These actions, at times, can lead to undesirable effects, such as hypotension, bradycardia, and Torsades de pointes (TdP).[L11265] In addition to the above, amiodarone may increase activity of peroxisome proliferator-activated receptors, leading to steatogenic changes in the liver or other organs.[A20227,A20650] Finally, amiodarone has been found to bind to the thyroid receptor due to its iodine content, potentially leading to amiodarone induced hypothyroidism or thyrotoxicosis.[A182168]', 'After intravenous administration, amiodarone acts to relax smooth muscles that line vascular walls, decreases peripheral vascular resistance (afterload), and increases the cardiac index by a small amount. Administration by this route also decreases cardiac conduction, preventing and treating arrhythmias.[A1901,A189666,L3561] When it is given orally, however, amiodarone does not lead to significant changes in the left ventricular ejection fraction. Similar to other anti-arrhythmic agents, controlled clinical trials do not confirm that oral amiodarone increases survival.[L3561] 



Amiodarone prolongs the QRS duration and QT interval.  In addition, a decreased SA (sinoatrial) node automaticity occurs with a decrease in AV node conduction velocity. Ectopic pacemaker automaticity is also inhibited.[L11265] Thyrotoxicosis or hypothyroidism may also result from the administration of amiodarone, which contains high levels of iodine, and interferes with normal thyroid function.[A182168]', 'DrugBank', 0, 0, 0);
