INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18955, 'Fentamat sandoz 50 mcg/hr 5 transdermal patch', 'فنتامات ساندوز 25 ميكروغرام / ساعة 5 لصقات جلدية', '350', NULL, 'Analgesics.opioid', 'Fentanyl', 'Hexal ag germany > novartis pharma sandoz division', 'Patch', 'لصقة', '50 mcg', '1', NULL, 'about fentanyl citrate synthetic primary opioid agonist anilidopiperidine derivative narcotic analgesic general anesthetic. mechanism of action of fentanyl citrate this drug primarily acts on the cns. fentanyl is a pure opioid agonist and primarily acts o', NULL, NULL, 811, '2022-07-16', 'Fentanyl intravenous or intramuscular injections are indicated for short term analgesia during induction, maintenance, and recovery from general or regional anesthesia.[Label] These injections are also used with a neuroleptic for premedication, induction, and as an adjunct to maintenance of anesthesia.[Label] Finally, fentanyl intravenous or intramuscular injections are used with oxygen for anesthesia in high risk patients.[Label]



Fentanyl sublingual tablets, transmucosal lozenges, buccal tablets, sublingual sprays, transdermal systems, and nasal sprays are indicated for the management of breakthrough pain in opioid tolerant cancer patients who require around the clock pain management.[L6598,L6601,L6604,L6607,L922,L6610]', 'Fentanyl binds to opioid receptors, especially the mu opioid receptor, which are coupled to G-proteins.[A179533] Activation of opioid receptors causes GTP to be exchanged for GDP on the G-proteins which in turn down regulates adenylate cyclase, reducing concentrations of cAMP.[A179533] Reduced cAMP decreases cAMP dependant influx of calcium ions into the cell.[A179533] The exchange of GTP for GDP results in hyperpolarization of the cell and inhibition of nerve activity.[A179533]', 'Fentanyl produces strong analgesia through its activation of opioid receptors.[Label,A179533] It has a duration of action of several hours and a wider therapeutic index as patients develop tolerance to opioids.[Label] Fentanyl is associated with a risk of addiction and abuse and should not be mixed with alcohol or benzodiazepines.[Label,L6598,L6601,L6604,L6607,L922,L6610,L6613]', 'DrugBank', 0, 0, 0);
