INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (10634, 'Sofenacin plus 5/50mg 30 f.c. tab', 'سوفيناسين بلس 5/50مجم 30 قرص', '408', NULL, 'Urinary incontinence.antispasmodics.muscarinic antagonists', 'Solifenacin succinate+mirabegron', 'Marcyrl co.', 'Tab', 'أقراص', '50mg', '3', 'علاج التبول اللارادي', NULL, '6223003573188', NULL, 4951, '2024-07-14', 'Solifenacin tablets are indicated to treat an overactive bladder with urinary incontinence, urgency, and frequency.[L7511]', 'Solifenacin is a competitive muscarinic receptor antagonist.[L7511] It has the highest affinity for M3, M1, and M2 muscarinic receptors.[A181247] 80% of the muscarinic receptors in the bladder are M2, while 20% are M3.[A181262] Solifenacin''s antagonism of the M3 receptor prevents contraction of the detrusor muscle, while antagonism of the M2 receptor may prevent contraction of smooth muscle in the bladder.[A181262]', 'Solifenacin antagonizes the M2 and M3 muscarinic receptors in the bladder to treat an overactive bladder.[L7511,A181262] It has a long duration of action as it is usually taken once daily.[L7511] Patients taking solifenacin should be aware of the risks of angioedema and anaphylaxis.[L7511]', 'DrugBank', 0, 0, 0);
