INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (12174, 'Venlaxine 75mg 10 f.c.tab.', 'فينلاكسين 75 مجم 10 اقراص', '41', NULL, 'Psychiatric.antidepressants', 'Venlafaxine', 'Amoun > rexcel', 'Tab', 'أقراص', '75mg', '1', 'لعلاج الامراض النفسية والعصبية -- مضاد للإكتئاب', NULL, NULL, NULL, 2178, '2022-05-19', 'Venlafaxine is indicated for the management of major depressive disorder (MDD), generalized anxiety disorder (GAD), social anxiety disorder (SAD), and panic disorder.[L43030]', 'The exact mechanism of action of venlafaxine in the treatment of various psychiatric conditions has not been fully elucidated; however, it is understood that venlafaxine and its active metabolite O-desmethylvenlafaxine (ODV) potently and selectively inhibits the reuptake of both serotonin and norepinephrine at the presynaptic terminal.[A27600,A252065,L43030] This results in increased levels of neurotransmitters available at the synapse that can stimulate postsynaptic receptors.[A252075] It is suggested that venlafaxine has a 30-fold selectivity for serotonin compared to norepinephrine: venlafaxine initially inhibits serotonin reuptake at low doses, and with higher doses, it inhibits norepinephrine reuptake in addition to serotonin.[A252065,A252070] Venlafaxine and ODV are also weak inhibitors of dopamine reuptake.[L43030]', 'Venlafaxine is an antidepressant agent that works to ameliorate the symptoms of various psychiatric disorders by increasing the level of neurotransmitters in the synapse. Venlafaxine does not mediate muscarinic, histaminergic, or adrenergic effects.[L43030]', 'DrugBank', 0, 0, 0);
