INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19449, 'Fucidin 2% ointment 20 gm', 'فيوسيدين 2% مرهم 20 جرام', '69', '37', 'Antibiotic', 'Fusidic acid', 'Mina pharm > leo pharmaceutical products', 'Ointment', 'مرهم', '2%', '1', NULL, 'about fusidic acid a bacteriostatic steroid antibiotic systemic antibacterial anti-infective. mechanism of action of fusidic acid fusidic acid is a bactericidal or bacteriostatic drug depending upon the concentration. it inhibits the transfer of aminoacid', '6222003702543', NULL, 963, '2024-11-26', 'For the treatment of bacterial infections.', 'Fusidic acid works by interfering with bacterial protein synthesis, specifically by preventing the translocation of the elongation factor G (EF-G) from the ribosome. It also can inhibit chloramphenicol acetyltransferase enzymes.', 'Fusidic acid is a bacteriostatic antibiotic and helps prevent bacterial growth while the immune system clears the infection.', 'DrugBank', 0, 0, 0);
