INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (29816, 'Zadmicin 0.1% topical oint 20 gm', 'زادميسين 0.1% مرهم موضعي 20 جرام', '26', '5.25', 'Aminoglycoside antibiotic', 'Gentamicin', 'Zad', 'Oint', 'مرهم', '0.1%', '1', 'مضاد حيوي يستخدم بشكل موضعي لعلاج التهابات الجلد، مثل القوباء والتهاب الجريبات، أو لعلاج بعض الأمراض الجلدية، مثل الأكزيما، والصدفية، والحروق، والجروح.', 'about gentamicin aminoglycoside antibiotic. mechanism of action of gentamicin gentamicin exerts its bactericidal action against gram negative organisms & some of gram positive organisms by inhibiting bacterial protein synthesis. the process involves 1. pe', '6224007855201', NULL, 5734, '2024-10-18', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Gentamicin is a bactericidal aminoglycoside that was discovered and isolated from _Micromonospora purpurea_ in 1963.[A234349] It is one of the most frequently prescribed aminoglycosides due to its spectrum of activity, low cost, and availability.[A234339,A234354] Gentamicin is effective against both gram-positive and gram-negative organisms but is particularly useful for the treatment of severe gram-negative infections including those caused by _Pseudomonas aeruginosa_.[A233325,A234359,A234364] There is the added benefit of synergy when gentamicin is co-administered with other antibacterials such as beta-lactams.[A234364] This synergistic activity is not only important for the treatment of complex infections, but can also contribute to dose optimization and reduced adverse effects.[A234359,A234364]

Although gentamicin is well-established and may be used in a variety of clinical applications, it is also associated with severe adverse effects including nephrotoxicity and ototoxicity which may limit its use.[A234369]', '["S01AA11", "S02AA14", "D06AX07", "S03AA06", "J01GB03"]', '[]');
