INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (30297, 'Celeborg 200 mg 20 caps.', 'سيليبورج 200مجم 20 كبسولة', '99', '78', 'Nsaid.selective cox-2 inhibitors', 'Celecoxib', 'Borg', 'Cap', 'كبسولة', '200 mg', '2', NULL, 'about celecoxib non-steroidal anti inflammatory drug (nsaid) cox-2 selective inhibitor anti-inflammatory agent. mechanism of action of celecoxib this inhibits prostaglandin synthesis peripherally by selective noncompetitive inhibition of cyclooxygenase-2', '6223002571437', NULL, 7244, '2024-12-04', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Celecoxib, a selective cyclooxygenase-2 (COX-2) inhibitor, is a nonsteroidal anti-inflammatory drug (NSAID) which is known for its decreased risk of causing gastrointestinal bleeding compared to other NSAIDS.[A181544] It is used to manage symptoms of various types of arthritis pain and in familial adenomatous polyposis (FAP) to reduce precancerous polyps in the colon.[A181532] It is marketed by Pfizer under the brand name Celebrex, and was initially granted FDA approval in 1998.[L7604] 

Interestingly, selective COX-2 inhibitors (especially celecoxib), have been evaluated as potential cancer chemopreventive and therapeutic drugs in clinical trials for a variety of malignancies.[A34124]', '["N02AJ16", "M01AH01", "G01AE10", "L01XX33", "C08CA51"]', '[]');
