INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (30973, 'Neopression 2mg 20 f.c.tabs.', 'نيوبريشن 2مجم 20 قرص', '130', '97', 'Psychiatric.antipsychotics', 'Brexpiprazole', 'Marcyrl', 'Tab', 'أقراص', '2mg', '2', 'امراض نفسية ومضاد للذهان', NULL, '6223003570675', NULL, 18775, '2024-11-20', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Brexpiprazole is a novel D2 dopamine and serotonin 1A partial agonist, called serotonin-dopamine activity modulator (SDAM), and a potent antagonist of serotonin 2A receptors, noradrenergic alpha 1B and 2C receptors. Brexpiprazole is approved for the treatment of schizophrenia, and as an adjunctive treatment for major depressive disorder (MDD). Although it failed Phase II clinical trials for ADHD, it has been designed to provide improved efficacy and tolerability (e.g., less akathisia, restlessness and/or insomnia) over established adjunctive treatments for major depressive disorder (MDD).', '["N05AX16"]', '[]');
