INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (31398, 'Spinobac 5mg/5ml syrup 120ml', 'سبينوباك 5مجم/5مل شراب 120 مل', '39', '25.5', 'Cns.analgesic.skeletal muscle relaxant', 'Baclofen', 'Saja pharmaceuticals > clavita pharma', 'Syrup', 'شراب', '5mg', '1', 'مسكن وباسط عضلات', 'about baclofen derivative of gaba chlorophenyl derivative skeletal muscle relaxant antispastic agent and in trigeminal neuralgia. mechanism of action of baclofen it inhibits both monosynaptic and polysynaptic reflexes at the spinal level by stimulating th', '6225000160484', NULL, 3807, '2024-09-26', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Baclofen is a gamma-aminobutyric acid (GABA) agonist used as a skeletal muscle relaxant. Although originally designed in 1962 to treat epilepsy, baclofen was not effective in treating this condition but instead was shown to reduce spasticity in selected patients.[A245338] Baclofen was reintroduced in 1971 as a treatment for spasticity and was later approved by the FDA in 1977.[A245323, A245338] Baclofen is used to manage severe muscle spasms of cerebral or spinal cord origins, including multiple sclerosis and traumatic brain injury.[L39429]

Baclofen was investigated for use in alcohol dependence and withdrawal; however, evidence is limited and there is inconsistent evidence to suggest its clinical efficacy in managing alcohol dependence or withdrawal symptoms.[A173905, A173908, A245338]', '["M03BX01"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (31400, 'Sprialypsy 500 mg 30 oral disintegrating tabs.', 'سبيراليبسي 500مجم 30 قرص ذائب بالفم', '138', '84', 'Anti-epileptic.pyrrolidine', 'Levetiracetam', 'Mash premiere > sas pharma', 'Tab', 'أقراص', '500 mg', '3', 'مضاد للصرع', 'about levetiracetam it is s-enantiomer of etiracetam. anticonvulsant medication used to treat epilepsy. mechanism of action of levetiracetam the exact mechanism for levetiracetam is unknown. however the drug binds to a synaptic vesicle protein sv2a which', NULL, NULL, 1085, '2023-09-29', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Levetiracetam is a drug within the pyrrolidine class that is used to treat various types of seizures stemming from epileptic disorders. It was first approved for use in the United States in 1999 and is structurally and mechanistically unrelated to other anti-epileptic drugs (AEDs).[L8606,L8600,L8615] Levetiracetam possesses a wide therapeutic index[L8615,A185918] and little-to-no potential to produce, or be subject to, pharmacokinetic interactions[L8606,L8600,L8615] - these characteristics make it a desirable choice over other AEDs, a class of drugs notorious for having generally narrow therapeutic indexes and a propensity for involvement in drug interactions.[A185927]', '["N03AX14"]', '[]');