INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19254, 'Fabuglycina syrup 120 ml', 'فابوجليسينا شراب 120 مل', '45', NULL, 'Iron supplement', 'Iron', 'Praxo pharm', 'Syrup', 'شراب', '120 ml', '1', NULL, NULL, NULL, NULL, 801, '2022-07-18', 0, 'For treatment of patients with documented iron deficiency in whom oral administration is unsatisfactory or impossible. Also used to replenish body iron stores in Non-Dialysis Dependent-Chronic Kidney Disease (NDD-CKD) patients receiving or not receiving erythropoietin and in Hemodialysis Dependent (HDD-CKD) and Peritoneal Dialysis Dependent (PDD-CKD) - Chronic Kidney Disease patients receiving an erythropoietin.', 'After iron dextran is injected, the circulating iron dextran is removed from the plasma by cells of the reticuloendothelial system, which split the complex into its components of iron and dextran. The iron is immediately bound to the available protein moieties to form hemosiderin or ferritin, the physiological forms of iron, or to a lesser extent to transferrin. This iron which is subject to physiological control replenishes hemoglobin and depleted iron stores.', 'Iron dextran is a dark brown, slightly viscous sterile liquid complex of ferric hydroxide and dextran for intravenous or intramuscular use. It is for treatment of patients with documented iron deficiency in whom oral administration is unsatisfactory or impossible. Iron is essential to the formation of hemoglobin and to the function and formation of other heme and nonheme compounds. Untreated depletion of iron stores leads to iron-deficient erythropoiesis and, in turn, to iron deficiency anemia.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19255, 'Flux free 16 chewable tab', 'فلاكس فري 16 قرص للمضغ', '9', NULL, 'Antacid', 'Aluminium hydroxide+ magnesium trisilicate + sodium alginate', 'Alexandria > millenium pharma-egypt', 'Tab', 'أقراص', NULL, '2', 'مضاد للحموضة لعلاح وتخفيف حالات حرقان المعدة', NULL, NULL, NULL, 1059, '2022-10-08', 0, NULL, 'Aluminum Acetate is an astringent.  An astrignent is a chemical that tends to shrink or constrict body tissues, usually locally after topical medicinal application.  The shrinkage or constriction is through osmotic flow of water (or other fluids) away from the area where the astringent was applied.  Astringent medicines cause shrinkage of mucous membranes or exposed tissues and are often used internally to check discharge of blood serum or mucous secretions. This can happen with a sore throat, hemorrhages, diarrhea, or with peptic ulcers. Externally applied astringents, which cause mild coagulation of skin proteins, dry, harden, and protect the skin. Acne sufferers are often advised to use astringents if they have oily skin. Astringents also help heal stretch marks and other scars. Mild astringent solutions are used in the relief of such minor skin irritations as those resulting from superficial cuts, allergies, insect bites, or fungal infections such as athlete''s foot. ', NULL, 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19256, 'Fluxamine 100mg 10 f.c.tab.', 'فلوكسامين 100مجم 10 اقراص', '30', NULL, 'Antidepressant', 'Fluvoxamine maleate', 'Pharopharma > pharma dessau gmbh - germany', 'Tab', 'أقراص', '100mg', '1', NULL, 'description fluvoxamine is an antidepressant which functions pharmacologically as a selective serotonin reuptake inhibitor. though it is in the same class as other ssri drugs it is most often used to treat obsessive-compulsive disorder. indication for man', '6221094036001', NULL, 1101, '2022-12-05', 0, 'Indicated predominantly for the management of depression and for Obsessive Compulsive Disorder (OCD) [FDA Label]. Has also been used in the management of bulimia nervosa [A250].', 'The exact mechanism of action of fluvoxamine has not been fully determined, but appears to be linked to its inhibition of CNS neuronal uptake of serotonin [FDA Label, A249, A250]. Fluvoxamine blocks the reuptake of serotonin at the serotonin reuptake pump of the neuronal membrane, enhancing the actions of serotonin on 5HT<sub>1A</sub> autoreceptors [FDA Label, A249, A250]. Studies have also demonstrated that fluvoxamine has virtually no affinity for α<sub>1</sub>- or α<sub>2</sub>-adrenergic, β-adrenergic, muscarinic, dopamine D<sub>2</sub>, histamine H<sub>1</sub>, GABA-benzodiazepine, opiate, 5-HT<sub>1</sub>, or 5-HT<sub>2</sub> receptors, despite having an affinity for binding to σ1 receptors [A250].', 'Fluvoxamine, an aralkylketone-derivative agent [FDA Label], is one of a class of antidepressants known as selective serotonin reuptake inhibitors (SSRIs) that differs structurally from other SSRIs [A249]. It is used to treat the depression associated with mood disorders. It is also used on occassion in the treatment of body dysmorphic disorder and anxiety [A249]. The antidepressant, antiobsessive-compulsive, and antibulimic actions of Fluvoxamine are presumed to be linked to its inhibition of CNS neuronal uptake of serotonin [FDA Label, A249, A250]. <i>In vitro</i> studies show that Fluvoxamine is a potent and selective inhibitor of neuronal serotonin reuptake and has only very weak effects on norepinephrine and dopamine neuronal reuptake [A250]. Moreover, apart from binding to σ1 receptors [A250], fluvoxamine has no significant affinity for adrenergic (alpha1, alpha2, beta), cholinergic, GABA, dopaminergic, histaminergic, serotonergic (5HT<sub>1A</sub>, 5HT<sub>1B</sub>, 5HT<sub>2</sub>), or benzodiazepine receptors; antagonism of such receptors has been hypothesized to be associated with various anticholinergic, sedative, and cardiovascular effects for other psychotropic drugs [A249]. Furthermore, some studies have demonstrated that the chronic administration of Fluvoxamine was found to downregulate brain norepinephrine receptors (as has been observed with other drugs effective in the treatment of major depressive disorder), while others suggest the opposite [A31914].', 'DrugBank', 0, 0, 0);
