INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (31554, 'Vasotrent 400 sr 20 f.c.tabs.', 'فازوترينت 400مجم 20 قرص ممتد المفعول', '82', '56', 'Anti-ischemic', 'Pentoxifylline', 'Alexandria > acdima international trading', 'Tab', 'أقراص', NULL, '2', NULL, 'about pentoxifylline a xanthine derivative phosphodiesterase inhibitor blood viscosity reducing agent. mechanism of action of pentoxifylline the drug exerts its pharmacological action by improving rheological (flow) properties of blood. it improves blood', '6224000588465', NULL, 1644, '2025-11-30', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Pentoxifylline (PTX) is a synthetic dimethylxanthine derivative that modulates the rheological properties of blood and also has both anti-oxidant and anti-inflammatory properties.[A226415, L30300] Although originally developed to treat intermittent claudication, a form of exertion-induced leg pain common in patients with peripheral arterial disease, PTX has been investigated for its possible use in diverse conditions, including osteoradionecrosis, diabetic kidney disease, and generally any condition associated with fibrosis.[A226410, A226415, A226455] More recently, PTX has been suggested as a possible treatment for COVID-19-induced pulmonary complications due to its ability to regulate the production of inflammatory cytokines.[A226608]

Pentoxifylline has been marketed in Europe since 1972; PTX extended-release tablets sold under the trade name TRENTAL by US Pharm Holdings were first approved by the FDA on Aug 30, 1984, but have since been discontinued. A branded product, PENTOXIL, marketed by Upsher-Smith Laboratories, and generic forms marketed by Valeant Pharmaceuticals and APOTEX have been available since the late 1990s.[L30300]', '["C04AD03", "R03DA20"]', '[]');
