INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (31767, 'Andomatoid 2 mg 28 f.c.tabs.', 'اندوماتويد 2مجم 28 قرص', '1,100', NULL, 'Janus kinase (jak) inhibitor', 'Baricitinib', 'Eva pharma > andalous pharma', 'Tab', 'أقراص', '2 mg', '4', 'يستخدم لعلاج التهاب المفاصل الروماتويدي (الحالة التي يهاجم فيها الجسم مفاصله مما يسبب الألم والتورم وفقدان الوظيفة).', 'olumiant is a prescription medicine used to treat adults with moderately to severely active rheumatoid arthritis (ra) who have tried at least one other medicine called a tumor necrosis factor (tnf) antagonist that did not work well enough or could not be', NULL, NULL, 3565, '2023-04-27', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Baricitinib is a Janus kinase (JAK) inhibitor. JAKs are tyrosine protein kinases that play an important role in pro-inflammatory signaling pathways. Overactive JAKs have been implicated in autoimmune disorders, such as rheumatoid arthritis. By inhibiting the actions of JAK1 and JAK2, baricitinib attenuates JAK-mediated inflammation and immune responses.[L41760]

Baricitinib was first approved by the European Commission (EC) in February 2017 for the treatment of rheumatoid arthritis in adults [A248395] and was later approved by the FDA in 2018.[A248400] The EC later approved baricitinib for the treatment of atopic dermatitis, making it the first JAK inhibitor used for this indication in Europe.[A248405] While baricitinib was granted emergency use as a treatment for COVID-19 in combination with [remdesivir] under the Emergency Use Authorization (EUA) in November 2020,[L22619] the FDA fully approved the use of baricitinib for the treatment of COVID-19 in May 2022.[L41760]', '["L04AA37"]', '[]');
