INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (31916, 'Rinvoq 15 mg 30 prolonger rel. tabs.', 'رينفوك 15مجم 30 قرص', '23,658', '17649', NULL, 'Upadacitinib', 'Abbvie ltd. > abbvie scientific office', 'Tab', 'أقراص', '15 mg', '3', NULL, 'rinvoq is a janus kinase (jak) inhibitor indicated for the treatment of: -rheumatoid arthritis. -psoriatic arthritis. -atopic dermatitis. -ulcerative colitis. -ankylosing spondylitis.', '300742306300', NULL, 27645, '2024-11-11', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Upadacitinib is an oral Janus kinase (JAK)1-selective inhibitor and a disease-modifying antirheumatic drug (DMARD) used in the treatment of rheumatoid arthritis to slow down disease progression. Rheumatoid arthritis is a chronic autoimmune inflammatory disease affecting the peripheral joints. It is characterized by synovial inflammation and hyperplasia, autoantibody production, cartilage damage and bone destruction, leading to co-morbidities.[A189165] Despite a variety of therapeutic agents available for treatment, up to 40% of the patients do not respond to current therapies, including biological therapies.[A189171] The etiology of the disease is mostly unknown; however, the role of JAK as a driver of immune-mediated conditions was discovered, leading to the use of JAK as therapeutic targets for rheumatoid arthritis.[A189168] To reduce dose-related toxicity (as seen with some pan-JAK inhibitors) without significantly affecting efficacy, more selective JAK1 inhibitors, upadacitinib and [filgotinib], were developed.[A189165]

The FDA approved upadacitinib in August 2019 and it is used for the treatment of active rheumatoid arthritis, psoriatic arthritis, atopic dermatitis, ulcerative colitis, and ankylosing spondylitis.[L10896] In December 2019, it was additionally approved by the European Commission.[L10899] Upadacitinib is marketed under the brand name RINVOQ for oral administration.[L10896]', '["L04AA44"]', '[]');
