INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (32707, 'Daptomycin 500 mg pwd. for i.v. inj./inf. vial', 'دابتوميسين 500مجم فيال', '1,859', '1300', 'Cyclic lipopeptide antibacterial agent', 'Daptomycin', 'Sandoz', 'Inj', 'حقن', '500 mg', '1', NULL, 'indications and usage: daptomycin for injection is a lipopeptide antibacterial indicated for the treatment of: ï¿½ complicated skin and skin structure infections (csssi) in adult patients. ï¿½ staphylococcus aureus bloodstream infections (bacteremia) in a', NULL, NULL, 772, '2025-07-16', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Daptomycin is a cyclic lipopeptide antibacterial agent with a broad spectrum of activity against Gram-positive bacteria, including methicillin-susceptible and -resistant _Staphylococcus aureus_ (MSSA/MRSA) and vancomycin-resistant Enterococci (VRE).[A231379, A231384, L32534] Chemically, daptomycin comprises 13 amino acids, including several non-standard and D-amino acids, with the C-terminal 10 amino acids forming an ester-linked ring and the N-terminal tryptophan covalently bonded to decanoic acid.[A231374, L32534] Daptomycin was first discovered in the early 1980s by researchers at Eli Lilly in soil samples from Mount Ararat in Turkey.[A231384] Early work on developing daptomycin was abandoned due to observed myopathy but was resumed in 1997 when Cubist Pharmaceuticals Inc. licensed daptomycin; it was found that a once-daily dosing scheme reduced side effects while retaining efficacy.[A231379]

