INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (32786, 'Elballerge 30mg/5ml susp. 100 ml', 'الباليرج 30مجم/5مل معلق 100 مل', '33', '22', 'Anti-histamine.anti-allergy', 'Fexofenadine', 'Apex pharma', 'Susp', 'معلق', '30mg', '1', 'مضاد للحساسية يستخدم فى تخفيف اعراض البرد والارتيكاريا', NULL, '6223003206598', NULL, 3822, '2025-06-01', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Fexofenadine is an over-the-counter second-generation antihistamine used in the treatment of various allergic symptoms.[L4269] It is selective for the H1receptor, carries little-to-no activity at off-targets, and does not cross the blood-brain barrier[L10779] - this is in contrast to previous first-generation antihistamines, such as [diphenhydramine], which readily bind to off-targets that contribute to side effects such as sedation.[A1452] Fexofenadine is the major active metabolite of [terfenadine][A1495] and is administered as a racemic mixture in which both enantiomers display approximately equivalent antihistamine activity.[L10779]', '["R06AX26"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (32789, 'Elixozolid 2mg/ml iv sol. 300 ml', 'اليكسوزوليد 2مجم/مل .30مل', '255', '178', 'Antibiotic.oxazolidinone', 'Linezolid', 'Nile pharma food', 'Sol', 'محلول', '2mg', '1', NULL, NULL, '6224010704169', NULL, 704, '2024-11-12', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Linezolid is a synthetic antibiotic which is used for the treatment of infections caused by aerobic Gram-positive bacteria. Its effects are bacteriostatic against both enterococci and staphylococci and bactericidal against most isolates of streptococci.[L32965] Linezolid exerts its antibacterial activity by inhibiting the initiation of bacterial protein synthesis - more specifically, it binds to the 23S ribosomal RNA of the 50S subunit[A11227,A199050] and, in doing so, prevents the formation of the 70S initiation complex which is essential for bacterial reproduction.

Linezolid was initially approved in 2000 and was the first member of the oxazolidinone antibiotic class.[A233425] A second member of this class, [tedizolid], was approved by the FDA in 2014 and is considered generally more effective and tolerable than its predecessor.', '["J01XX08"]', '[]');
