INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27256, 'Sologati 0.3% eye dps. 5 ml', 'سولوجاتي 0.3% قطرة للعين 5مل', '17', NULL, 'Antibiotic.quinolone', 'Gatifloxacin', 'Grand pharma > city pharma', 'Sol', 'محلول', '0.3%', '1', NULL, 'uses: gatifloxacin is used to treat a variety of bacterial infections. it works by stopping the growth of bacteria. this medication belongs to a class of drugs called quinolone antibiotics. this antibiotic only treats bacterial infections. it will not wor', NULL, NULL, 746, '2022-07-24', 'For the treatment of bronchitis, sinusitis, community-acquired pneumonia, and skin infections (abscesses, wounds) caused by S. pneumoniae, H. influenzae, S. aureus, M. pneumoniae, C. pneumoniae, L. pneumophila, S. pyogenes', 'The bactericidal action of Gatifloxacin results from inhibition of the enzymes topoisomerase II (DNA gyrase) and topoisomerase IV, which are required for bacterial DNA replication, transcription, repair, and recombination.', 'Gatifloxacin is a synthetic broad-spectrum 8-methoxyfluoroquinolone antibacterial agent for oral or intravenous administration. is bactericidal and its mode of action depends on blocking of bacterial DNA replication by binding itself to an enzyme called DNA gyrase, which allows the untwisting required to replicate one DNA double helix into two. Notably the drug has 100 times higher affinity for bacterial DNA gyrase than for mammalian. Gatifloxacin is a broad-spectrum antibiotic that is active against both Gram-positive and Gram-negative bacteria. It should be used only to treat or prevent infections that are proven or strongly suspected to be caused by bacteria.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19239, 'Fluroflox 400mg 5 f.c. tabs.', 'فلوروفلوكس 400مجم 5 قرص', '92', '77.75', 'Antibiotic.quinolone', 'Moxifloxacin', 'Global napi pharmaceuticals > ta pharma', 'Tab', 'أقراص', '400mg', '1', 'مضاد حيوي', 'about moxifloxacin a third generation synthetic fluoroquinolone antibiotic a broad spectrum antibiotic. mechanism of action of moxifloxacin moxifloxacin acts by inhibiting the enzyme dna gyrase (topoisomerase 2) and topoisomerase 4.dna gyrase helps in the', '6225000351110', NULL, 11194, '2024-06-29', 'For the treatment of sinus and lung infections such as sinusitis, pneumonia, and secondary infections in chronic bronchitis. Also for the treatment of bacterial conjunctivitis (pinkeye).', 'The bactericidal action of moxifloxacin results from inhibition of the enzymes topoisomerase II (DNA gyrase) and topoisomerase IV. DNA gyrase is an essential enzyme that is involved in the replication, transcription and repair of bacterial DNA. Topoisomerase IV is an enzyme known to play a key role in the partitioning of the chromosomal DNA during bacterial cell division.', 'Moxifloxacin is a quinolone/fluoroquinolone antibiotic. Moxifloxacin can be used to treat infections caused by the following bacteria: Aerobic Gram-positive microorganisms: _Corynebacterium_ species, _Micrococcus luteus_, _Staphylococcus aureus_, _Staphylococcus epidermidis_, _Staphylococcus haemolyticus_, _Staphylococcus hominis_, _Staphylococcus warneri_, _Streptococcus pneumoniae_, and _Streptococcus viridans_ group. Aerobic Gram-negative microorganisms: _Acinetobacter lwoffii_, _Haemophilus influenzae_, and _Haemophilus parainfluenzae_. Other microorganisms: _Chlamydia trachomatis_. Moxifloxacin is bactericidal and its mode of action depends on blocking of bacterial DNA replication by binding itself to an enzyme called DNA gyrase, which allows the untwisting required to replicate one DNA double helix into two. Notably the drug has 100 times higher affinity for bacterial DNA gyrase than for mammalian. Moxifloxacin is a broad-spectrum antibiotic that is active against both Gram-positive and Gram-negative bacteria.', 'DrugBank', 0, 0, 0);
