INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (29213, 'Vexamod 37.5mg 10 s.r. caps.', 'فيكسامود 37.5مجم 10 كبسولات', '20', NULL, 'Psychiatric.antidepressants', 'Venlafaxine', 'Sigma', 'Cap', 'كبسولة', '37.5mg', '1', NULL, 'about venlafaxine an arylalkanolamine derivative serotonin/norepinephrine reuptakeinhibitor antidepressant anxiolytic. mechanism of action of venlafaxine venlafaxine and its metabolite block the re-uptake of neurotransmitter serotonin and noradrenaline fr', NULL, NULL, 825, '2022-07-16', 'Venlafaxine is indicated for the management of major depressive disorder (MDD), generalized anxiety disorder (GAD), social anxiety disorder (SAD), and panic disorder.[L43030]', 'The exact mechanism of action of venlafaxine in the treatment of various psychiatric conditions has not been fully elucidated; however, it is understood that venlafaxine and its active metabolite O-desmethylvenlafaxine (ODV) potently and selectively inhibits the reuptake of both serotonin and norepinephrine at the presynaptic terminal.[A27600,A252065,L43030] This results in increased levels of neurotransmitters available at the synapse that can stimulate postsynaptic receptors.[A252075] It is suggested that venlafaxine has a 30-fold selectivity for serotonin compared to norepinephrine: venlafaxine initially inhibits serotonin reuptake at low doses, and with higher doses, it inhibits norepinephrine reuptake in addition to serotonin.[A252065,A252070] Venlafaxine and ODV are also weak inhibitors of dopamine reuptake.[L43030]', 'Venlafaxine is an antidepressant agent that works to ameliorate the symptoms of various psychiatric disorders by increasing the level of neurotransmitters in the synapse. Venlafaxine does not mediate muscarinic, histaminergic, or adrenergic effects.[L43030]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23963, 'Ocuseellerge 0.05% eye drops 5 ml', 'اوكيوسيليرج 0.05% قطرة للعين 5مل', '30', '19.25', 'H1- and h2-receptors blocker', 'Epinastine', 'Rameda > talent pharma', 'Drops', 'نقط', '0.05%', '1', NULL, 'description epinastine is used for the prevention of itching associated with allergic conjunctivitis. it has a multi-action effect that inhibits the allergic response in 3 ways: 1. stabilizes mast cells by preventing mast cell degranulation to control the', '6225000351639', NULL, 1846, '2024-11-20', 'For the prevention of itching associated with allergic conjunctivitis.', 'Epinastine has a multiaction effect that inhibits the allergic response in 3 ways: 1. stabilizes mast cells by preventing mast cell degranulation to control the allergic response, 2. prevents histamine binding to both the H1- and H<sub>2</sub>-receptors to stop itching and provide lasting protection, and 3. prevents the release of proinflammatory chemical mediators from the blood vessel to halt progression of the allergic response.', 'Epinastine is an antihistamine and an inhibitor of histamine release from the mast cell for topical administration to the eyes. Epinastine is indicated for the prevention of itching associated with allergic conjunctivitis. Epinastine is a topically active, direct H<sub>1</sub>-receptor antagonist and an inhibitor of the release of histamine from the mast cell. Epinastine is selective for the histamine H<sub>1</sub>-receptor and has affinity for the histamine H2 receptor. Epinastine also possesses affinity for the a1-, a2-, and 5-HT<sub>2</sub> -receptors. Epinastine does not penetrate the blood/brain barrier and, therefore, is not expected to induce side effects of the central nervous system.', 'DrugBank', 0, 0, 0);
