INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (9650, 'Parnetil 10 mg 10 tab', 'بارنيتيل 10 مجم 10 قرص', '4', NULL, 'Psychiatric.antidepressants', 'Tranylcypromine', 'Kahira', 'Tab', 'أقراص', '10 mg', '1', 'مضاد للاكتئاب -- علاج نوبات الهلع -- علاج الوسواس القهري', NULL, NULL, NULL, 1047, '2022-05-28', 'For the treatment of major depressive episode without melancholia.', 'Tranylcypromine irreversibly and nonselectively inhibits monoamine oxidase (MAO). Within neurons, MAO appears to regulate the levels of monoamines released upon synaptic firing. Since depression is associated with low levels of monoamines, the inhibition of MAO serves to ease depressive symptoms, as this results in an increase in the concentrations of these amines within the CNS.', 'Tranylcypromine belongs to a class of antidepressants called monoamine oxidase inhibitors (MAOIs). Tranylcypromine is a non-hydrazine monoamine oxidase inhibitor with a rapid onset of activity. MAO is an enzyme that catalyzes the oxidative deamination of a number of amines, including serotonin, norepinephrine, epinephrine, and dopamine. Two isoforms of MAO, A and B, are found in the body. MAO-A is mainly found within cells located in the periphery and catalyzes the breakdown of serotonin, norepinephrine, epinephrine, dopamine and tyramine. MAO-B acts on phenylethylamine, norepinephrine, epinephrine, dopamine and tyramine, is localized extracellularly and is found predominantly in the brain. While the mechanism of MAOIs is still unclear, it is thought that they act by increasing free serotonin and norepinephrine concentrations and/or by altering the concentrations of other amines in the CNS. It has been postulated that depression is caused by low levels of serotonin and/or norepinephrine and that increasing serotonergic and norepinephrinergic neurotransmission results in relief of depressive symptoms. MAO A inhibition is thought to be more relevant to antidepressant activity than MAO B inhibition. Selective MAO B inhibitors, such as selegiline, have no antidepressant effects.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27189, 'Sodium chloride 0.9% (fipco) i.v. inf. 500 ml (rubber cap)', 'صوديوم كلوريد 0.9% فيبكو محلول ملح وريدي بغطاء مطاطي 500 مل', '15', NULL, 'Normal saline', 'Sodium chloride', 'El-fath (fipco)', 'Cap', 'كبسولة', '0.9%', '1', NULL, NULL, NULL, NULL, 1050, '2022-07-11', 'This intravenous solution is indicated for use in adults and pediatric patients as a source of electrolytes and water for hydration. Also, designed for use as a diluent and delivery system for intermittent intravenous administration of compatible drug additives. ', 'Sodium and chloride — major electrolytes of the fluid compartment outside of cells (i.e., extracellular) — work together to control extracellular volume and blood pressure. Disturbances in sodium concentrations in the extracellular fluid are associated with disorders of water balance.', 'Sodium, the major cation of the extracellular fluid, functions primarily in the control of water distribution, fluid balance, and osmotic pressure of body fluids. Sodium is also associated with chloride and bicarbonate in the regulation of the acid-base equilibrium of body fluid.

Chloride, the major extracellular anion, closely follows the metabolism of sodium, and changes in the acid-base balance of the body are reflected by changes in the chloride concentration. ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24355, 'Otoxin 3mg/ml ear drops', 'اوتوكسين 3مجم/مل نقط للاذن', '11', NULL, 'Antibiotic.quinolone', 'Ofloxacin', 'Sedico', 'Drops', 'نقط', '3mg', '1', NULL, 'about ofloxacin a second generation fluoroquinolone(quinolones) antibiotic. mechanism of action of ofloxacin ofloxacin is a bactericidal drug. it act by inhibiting the enzyme dna gyrase (topoisomerase 2) and topoisomerase 4.dna gyrase helps in the formati', NULL, NULL, 953, '2022-07-08', 'For the treatment of infections (respiratory tract, kidney, skin, soft tissue, UTI), urethral and cervical gonorrhoea.', 'Ofloxacin acts on DNA gyrase and toposiomerase IV, enzymes which, like human topoisomerase, prevents the excessive supercoiling of DNA during replication or transcription. By inhibiting their function, the drug thereby inhibits normal cell division.', 'Ofloxacin is a quinolone/fluoroquinolone antibiotic. Ofloxacin is bactericidal and its mode of action depends on blocking of bacterial DNA replication by binding itself to an enzyme called DNA gyrase, which allows the untwisting required to replicate one DNA double helix into two. Notably the drug has 100 times higher affinity for bacterial DNA gyrase than for mammalian. Ofloxacin is a broad-spectrum antibiotic that is active against both Gram-positive and Gram-negative bacteria.', 'DrugBank', 0, 0, 0);
