INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (33086, 'Laries solution 250 ml', 'لاريس محلول 250 مل', '120', '75', 'Disinfectant', 'Chlorhexidine', 'Hexa pharma international', 'Solution', 'محلول', '250 ml', '1', NULL, NULL, NULL, NULL, 756, '2024-08-20', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Chlorhexidine is a broad-spectrum antimicrobial biguanide used as a topical antiseptic and in dental practice for the treatment of inflammatory dental conditions caused by microorganisms.[L11512] It is one of the most common skin and mucous membrane antiseptic agents in use today.[A190417] The molecule itself is a cationic bis-guanide consisting of two 4-chlorophenyl rings and two biguanide groups joined by a central hexamethylene chain.[A190453] Topical chlorhexidine for disinfection, as well as oral rinses for dental use, carries activity against a broad range of pathogens including bacteria, yeasts, and viruses.[L11518,L11512,A190453]

Chlorhexidine was developed in the UK by Imperial Chemical Industries in the early 1950s[A190474] and was introduced to the US in the 1970s.[L11572] The FDA withdrew its approval for the use of chlorhexidine gluconate topical tincture 0.5%, due to a significant number of reports concerning chemical and thermal burns associated with the use of this product.[L43942,L44027] Other formulations of chlorhexidine continue to be available.', '["R02AA05", "S03AA04", "D08AC52", "D08AC02", "S01AX09", "D09AA12", "B05CA02", "A01AB03", "S02AA09"]', '[]');
