INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (17786, 'Dolomine 0.5gm/100ml eye drops 5 ml', 'دولومين 0.5 جم / 100 مل قطرة 5 مل', '6', NULL, 'Nsaid.acetic acid derivatives', 'Ketorolac tromethamine', 'Kahira', 'Drops', 'نقط', '0.5gm', '1', NULL, 'mechanism of action ketorolac is a nonsteroidal anti-inflammatory drug (nsaid) chemically related to indomethacin and tolmetin. ketorolac tromethamine is a racemic mixture of [-]s- and [+]r-enantiomeric forms with the s-form having analgesic activity. its', '6221068503379', NULL, 768, '2022-07-13', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Ketorolac is a Non-steroidal anti-inflammatory drug (NSAID) and is commercially available as an oral tablet, injectable, nasal spray and as an ophthalmic solution.[L6358][L6508][L11055][L3674][L11070] 

It''s analgesic properties make it a useful pain management tool across many settings including postoperative pain, rheumatoid arthritis, osteoarthritis, menstrual disorders, headaches, spinal and soft tissue pain, and ankylosing spondylitis.[L6520] Impressively, ketorolac has a similar efficacy to standard doses of morphine and meperidine making it a useful opioid sparing agent.[L6511]', '["M01AB15", "S01FB51", "S01BC05"]', '[]');
