INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (18223, 'Emedrotec 3mg 20 buccal bioadhesive tab.', 'ايمدروتيك 3 مجم 20 قرص', '16', NULL, 'Psychiatric.antipsychotics', 'Prochlorperazine', 'Eva pharma', 'Tab', 'أقراص', '3mg', '2', NULL, 'about prochlorperazine first-generation antipsychotic phenothiazine derivative antiemetic antipsychotic anxiolytic. mechanism of action of prochlorperazine antipsychotic action: prochlorperazine binds to the dopamine receptor (d1 d2 d3 &d4).it exerts its', '6223002451852', NULL, 879, '2022-07-13', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Prochlorperazine, also known as compazine, is a piperazine phenothiazine and first-generation antipsychotic drug that is used for the treatment of severe nausea and vomiting, as well as short-term management of psychotic disorders such as generalized non-psychotic anxiety and schizophrenia.[label] It mainly works by depressing the chemoreceptor trigger zone and blocking D2 dopamine receptors in the brain. It was shown to also block histaminergic, cholinergic and noradrenergic receptors.[L6637] Prochlorperazine was first developed in the 1950s [L6643] and was first approved by the FDA in 1956. Although newer antiemetic agents such as 5-HT3 antagonists are more heavily promoted, prochlorperazine is still widely used in nausea and vomiting.[L6640]', '["N05AB04"]', '[]');
