INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19946, 'Glufast 5mg 10 tab.', 'جلوفاست 5مجم 10 اقراص', '10', NULL, 'Anti-diabetic.secretagogues.glinides', 'Mitiglinide', 'Hikma pharma > kissei pharmaceutical co.ltd-japan', 'Tab', 'أقراص', '5mg', '1', 'علاج مرض السكر', 'indication for the treatment of type 2 diabetes. pharmacodynamics mitiglinide belongs to the meglitinide class of blood glucose-lowering drugs. it is approved for use in japan but has not yet gained fda approval. mechanism of action mitiglinide is thought', NULL, NULL, 727, '2022-05-31', 0, 'For the treatment of type 2 diabetes.', 'Mitiglinide is thought to stimulate insulin secretion by binding to and blocking ATP-sensitive K(+) (K(ATP)) channels (Kir6.2/SUR1 complex, KATP channels) in pancreatic beta-cells. Closure of potassium channels causes depolarization which stimulates calcium influx through voltage-gated calcium channels. High intracellular calcium subsequently triggers the exocytosis of insulin granules. ', 'Mitiglinide belongs to the meglitinide class of blood glucose-lowering drugs. It is approved for use in Japan but has not yet gained FDA approval.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19949, 'Glurenor 30mg 20 tab.', 'جلورينور 30مجم 20 قرص', '21', NULL, 'Anti-diabetic', 'Gliquidone', 'Mina pharm > menarini', 'Tab', 'أقراص', '30mg', '2', NULL, 'indication used in the treatment of diabetes mellitus type 2. mechanism of action the mechanism of action of gliquidone in lowering blood glucose appears to be dependent on stimulating the release of insulin from functioning pancreatic beta cells and incr', '6222003700990', NULL, 607, '2022-12-05', 0, 'Used in the treatment of diabetes mellitus type 2.', 'The mechanism of action of gliquidone in lowering blood glucose appears to be dependent on stimulating the release of insulin from functioning pancreatic beta cells, and increasing sensitivity of peripheral tissues to insulin. Gliquidone likely binds to ATP-sensitive potassium channel receptors on the pancreatic cell surface, reducing potassium conductance and causing depolarization of the membrane. Membrane depolarization stimulates calcium ion influx through voltage-sensitive calcium channels. This increase in intracellular calcium ion concentration induces the secretion of insulin.', 'Gliquidone is an anti-diabetic drug in the sulfonylurea class. In patients with diabetes mellitus, there is a deficiency or absence of a hormone manufactured by the pancreas called insulin. Insulin is the main hormone responsible for the control of sugar in the blood. Gliquidone is an antidiabetic medication which is used in those patients with adult maturity onset or non-insulin dependent diabetes (NIDDM). It works by lowering blood sugar levels by stimulating the production and release of insulin from the pancreas. It also promotes the movement of sugar from the blood into the cells in the body which need it.

