INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20038, 'Grandocaln 4 mg 10 extended rel. tabs.', 'جراندوكالن 4 مجم 10 أقراص', '20', NULL, 'Urinary incontinence.antispasmodics.muscarinic antagonists', 'Fesoterodine', 'Grand pharma > delta grand pharma', 'Tab', 'أقراص', '4 mg', '1', NULL, 'fesoterodine is an antimuscarinic drug to treat overactive bladder syndrome (oab). fesoterodine is a prodrug. it is broken down into its active metabolite 5-hydroxymethyl tolterodine by plasma esterases. fesoterodine has the advantage of allowing more fle', NULL, NULL, 749, '2022-07-25', 0, 'Fesoterodine is indicated for the treatment of overactive bladder in adult patients with symptoms of urge urinary incontinence, urgency, and frequency.[L37624] It is also indicated in the treatment of neurogenic detrusor overactivity in pediatric patients ≥6 years old weighing >25 kg.[L37624]', 'Fesoterodine, once converted to its active metabolite, 5-hydroxymethyltolterodine, acts as a competitive antagonists at muscarinic receptors. This results in the inhibition of bladder contraction, decrease in detrusor pressure, and an incomplete emptying of the bladder.', 'In-vivo the fesoteridine prodrug is broken down into its active metabolite, 5-hydroxymethyl tolterodine (5-HMT), by plasma esterases. The 5-hydroxymethyl metabolite, which exhibits an antimuscarinic activity. Both urinary bladder contraction and salivation are mediated via cholinergic muscarinic receptors. Therefore, acting as a competitive muscarinic receptor antagonist, fesoterodine ultimately acts to decrease the detrusor pressure by its muscarinic antagonism, thereby decreasing bladder contraction and consequently, the urge to urinate.', 'DrugBank', 0, 0, 0);
