INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20284, 'Healthtadine 10 mg 10 tabs.', 'هيلثتادين 10 مجم 10 أقراص', '26', NULL, 'Antihistamine.second-generation', 'Rupatadine', 'Pharmed healthcare', 'Tab', 'أقراص', '10 mg', '1', NULL, 'rupatadine fumarate has been approved for the treatment of allergic rhinitis and chronic urticaria in adults and children over 12 years. the defined daily dose (ddd) is 10 mg orally.', '6224007433133', NULL, 841, '2022-07-29', 0, 'For the symptomatic relief of nasal and non-nasal symptoms of seasonal allergic rhinitis and perennial allergic rhinitis in patients 2 years of age and older [FDA Label]. Also used for the symptomatic relief of chronic spontaneous urticaria in patients 2 years of age and older.', 'Rupatadine is a dual histamine H1 receptor and platelet activating (PAF) receptor antagonist [A19779] [FDA Label]. During allergic response mast cells undergo degranulation, releasing histamine and other substances [A19780]. Histamine acts on H1 receptors to produce symptoms of nasal blockage, rhinorhea, itching, and swelling. PAF is produced from phospholipids cleaved by phospholipase A2. It acts to produce vascular leakage which contributes to rhinorhea and nasal blockage. By blocking both the H1 receptor and PAF receptor, rupatidine prevents these mediators from exerting their effects and so reduces the severity of allergic symptoms.', 'Rupatadine is an anti allergenic and acts to reduce allergic symptoms like urticaria, rhinorrhea, sneezing and itching [FDA Label].', 'DrugBank', 0, 0, 0);
