INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22012, 'Longicef 250mg/5ml susp. 60ml', 'لونجسيف 250مجم/5مل معلق 60 مل', '10', NULL, 'Antibiotic.cephalosporin.first-generation', 'Cefadroxil', 'Kahira', 'Susp', 'معلق', '250mg', '1', NULL, 'about cefadroxil first generation cephalosporin para-hydroxy derivative of cefalexin oral broad-spectrum antibiotic mechanism of action cefadroxil is a 1st generation cephalosporin which exerts its bactericidal action against both gram positive & gram neg', '6221068201909', NULL, 770, '2023-04-18', 'For the treatment of the following infections (skin, UTI, ENT) caused by; <i>S. pneumoniae, H. influenzae, staphylococci, S. pyogenes</i> (group A beta-hemolytic streptococci), <i>E. coli, P. mirabilis, Klebsiella</i> sp, coagulase-negative staphylococci and <i>Streptococcus pyogenes</i>', 'Like all beta-lactam antibiotics, cefadroxil binds to specific penicillin-binding proteins (PBPs) located inside the bacterial cell wall, causing the inhibition of the third and last stage of bacterial cell wall synthesis. Cell lysis is then mediated by bacterial cell wall autolytic enzymes such as autolysins; it is possible that cefadroxil interferes with an autolysin inhibitor.', 'Cefadroxil, a first-generation cephalosporin antibiotic, is used to treat urinary tract infections, skin and skin structure infections, pharyngitis, and tonsillitis.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (6566, 'Avantofer', 'افانتوفير', '100', NULL, NULL, 'Liposomal ferric pyrophosphate+40 mg l-ascorbic acid+0.08 mg folic acid+0.0004 mg cyanocobalamin', 'Avantgarde pharma egypt', 'Unknown', 'غير محدد', NULL, '1', NULL, NULL, NULL, NULL, 971, '2025-08-08', 'Investigated for use/treatment in neuropathy (diabetic) and peripheral vascular disease.', 'The encapsulated formulation (Liprostin) has been shown to improve the therapeutic index of PGE-1, positively impacting many areas of treatment, such as heart attacks, occlusive disease, ischemic ulcers, critical limb salvage, claudicants, and arthritis.', NULL, 'DrugBank', 0, 0, 0);
