INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15738, 'Cancidas 50mg powder vial for inf.', 'كانسيداس 50 مجم بودرة فيال', '2,500', '2000', 'Antifungals.miscellaneous', 'Caspofungin', 'Merck sharp & dohme > soficopharm', 'Vial', 'فيال', '50mg', '1', NULL, 'indications and usage cancidas is an echinocandin antifungal drug indicated in adults and pediatric patients (3 months and older) for: * empirical therapy for presumed fungal infections in febrile neutropenic patients. * treatment of candidemia and the fo', NULL, NULL, 1197, '2023-12-20', 'For the treatment of esophageal candidiasis and invasive aspergillosis in patients who are refractory to or intolerant of other therapies.', 'Caspofungin inhibits the synthesis of beta-(1,3)-D-glucan, an essential component of the cell wall of <i>Aspergillus</i> species and <i>Candida</i> species. beta-(1,3)-D-glucan is not present in mammalian cells. The primary target is beta-(1,3)-glucan synthase.', 'Caspofungin is an antifungal drug, and belongs to a new class termed the echinocandins. It is used to treat <i>Aspergillus</i> and <i>Candida</i> infection, and works by inhibiting cell wall synthesis. Antifungals in the echinocandin class inhibit the synthesis of glucan in the cell wall, probably via the enzyme 1,3-beta glucan synthase. There is a potential for resistance development to occur, however <i>in vitro</i> resistance development to Caspofungin by Aspergillus species has not been studied.', 'DrugBank', 0, 0, 0);
