INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (18351, 'Entyvio 300 mg vial for inf.', 'انتيفيو 300مجم فيال للاطفال', '48,824', '36600', 'Ulcerative colitis', 'Vedolizumab', 'Takeda > pharma overseas', 'Vial', 'فيال', '300 mg', '1', NULL, 'indications: entyvio (vedolizumab) is indicated for adult patients with moderately to severely active ulcerative colitis (uc) or crohns disease (cd) who have had an inadequate response with were intolerant to or demonstrated dependence on corticosteroids', NULL, NULL, 2617, '2024-07-27', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Vedolizumab is a recombinant humanized IgG1 monoclonal antibody directed against the human lymphocyte α4β7 integrin, a key mediator of gastrointestinal inflammation. It is used in the treatment of moderate to severe active ulcerative colitis and Crohn''s disease for patients who have had an inadequate response with, lost response to, or were intolerant to inhibitors of tumor necrosis factor-alpha (TNF-alpha) or other conventional therapies. By blocking its primary target, α4β7 integrin, vedolizumab reduces inflammation in the gut. Vedolizumab is administered by IV infusion over a period of 30 minutes; after the first dose, it is given again at two and six weeks and then every 8 weeks thereafter.', '["L04AA33"]', '[]');
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (18352, 'Epanutin 100mg 50 caps', 'ايبانوتين 100مجم 50 كبسولة', '14', NULL, 'Anti-epileptic.hydantoin', 'Phenytoin', 'El nile. > pfizer', 'Cap', 'كبسولة', '100mg', '5', NULL, NULL, NULL, NULL, 3916, '2022-07-06', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Phenytoin is classified as a hydantoin derivative and despite its narrow therapeutic index, it is one of the most commonly used anticonvulsants.[A33595,A188832,A189219] Since it''s introduction about 80 years ago, phenytoin has not only been established as an effective anti-epileptic, but has also been investigated for several other indications such as bipolar disorder, retina protection, and wound healing.[A188826,A188832]

Clinicians are advised to initiate therapeutic drug monitoring in patients who require phenytoin since even small deviations from the recommended therapeutic range can lead to suboptimal treatment, or adverse effects.[A189219,A35884] Both parenteral and oral formulations of phenytoin are available on the market.[A189219]', '["N03AB02", "N03AB52"]', '[]');
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (18353, 'Epcidin s.r. 6 mg 10 caps.', 'ابسيدين اس ار 6 مجم 10 كبسولات', '8', NULL, 'Cns.analgesic.skeletal muscle relaxant.alpha2 agonist', 'Tizanidine', 'Epci', 'Cap', 'كبسولة', '6 mg', '1', 'مسكن وباسط للعضلات ومضاد للالتهابات', 'about tizanidine a centrally acting a-2 adrenergic agonist centrally acting muscle relaxant (short-acting). mechanism of action of tizanidine this drug is a centrally acting alpha2 agonist at alpha2-adrenergic receptor sites and presumably reduces spastic', NULL, NULL, 1058, '2022-07-22', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Tizanidine is a fast-acting drug used for the management of muscle spasm, which may result from the effects of multiple sclerosis, stroke, an acquired brain injury, or a spinal cord injury [T574].  It may also be caused by musculoskeletal injury [A177583].  Regardless of the cause, muscle spasticity can be an extremely painful and debilitating condition. 

Initially approved by the FDA in 1996, tizanidine is an Alpha-2 adrenergic receptor agonist reducing spasticity by the presynaptic inhibition of excitatory neurotransmitters that cause firing of neurons promoting muscle spasm [FDA label].', '["M03BX02"]', '[]');
