INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20503, 'Hipress-d 20 caps.', 'هايبرس دى 20 كبسولة', '11', NULL, 'Antihypertensive.beta blocker.selective b1', 'Amiloride+atenolol+hydrochlorothiazide', 'Rameda', 'Cap', 'كبسولة', NULL, '2', NULL, 'description : a cardioselective beta-adrenergic blocker possessing properties and potency similar to propranolol but without a negative inotropic effect. dose: adult: po- angina pectoris; htn- 50-100 mg once daily. migraine prophylaxis 50-100 mg/day. indi', NULL, NULL, 999, '2022-08-02', 0, NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20504, 'Hirsustop 11.5 % topical cream 15 gm', 'هيرسوستوب 11.5% كريم موضعي 15 جم', '75', NULL, 'Hirsutism', 'Eflornithine', 'Medizen pharmaceutical industries > omega pharmaceutical and chemical industries (opci)', 'Cream', 'كريم', '11.5 %', '1', NULL, 'used in treatment of facial hirsutism (excessive hair growth) as well as in african trypanosomiasis (sleeping sickness). eflornithine hydrochloride cream for topical application is meant for women suffering from facial hirsutism. eflornithine for injectio', NULL, NULL, 717, '2022-07-13', 0, 'Eflornithine is indicated to reduce the risk of relapse in adult and pediatric patients with high-risk neuroblastoma (HRNB) who have demonstrated at least a partial response to prior multiagent, multimodality therapy including anti-GD2 immunotherapy.[L49288]  It was also previously indicated for the treatment of female hirsutism and African trypanosomiasis but has since been discontinued.[L49318]', 'Eflornithine is an irreversible inhibitor of the enzyme ornithine decarboxylase (ODC), the first and rate-limiting enzyme in the biosynthesis of polyamines and a transcriptional target of MYCN. Polyamines are involved in the differentiation and proliferation of mammalian cells and are important for neoplastic transformation.[L49288] ', 'Inhibition of polyamine synthesis by eflornithine restored the balance of the LIN28/Let-7 metabolic pathway, which is involved in the regulation of cancer stem cells and glycolytic metabolism, by decreasing expression of the oncogenic drivers MYCN and LIN28B in MYCN-amplified neuroblastoma. In vitro, eflornithine induced senescence and suppressed neurosphere formation in MYCN-amplified and MYCN non-amplified neuroblastoma cells, indicating a cytostatic effect. Treatment with eflornithine prevented or delayed tumor formation in mice injected with limiting dilutions of MYCN-amplified neuroblastoma cells.[L49288]



Additionally, polyamines are also involved in keratin synthesis, and inhibition of polyamines can decrease the proliferation of hair matrix cells and thus inhibit the anagen phase of hair production.[A4113]



', 'DrugBank', 0, 0, 0);
