    ) VALUES (23322, 'Naviluca 2mg/ml vial for i.v. inf. 100 ml', 'نافيلوكا 2مجم/مل فيال 100مل', '147', '81', 'Antifungals.triazoles', 'Fluconazole', 'Arabcomed-egypt > rotabiogen for pharmaceutical invest.', 'Vial', 'فيال', '2mg', '1', NULL, 'Fluconazole can be administered in the treatment of the following fungal infections[L11043]:



