INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1439, 'Betaferon 250 mcg/ml pd. for solutn. for s.c. inj.', 'بيتافيرون 250مكجم/مل باودر لعمل محلول للحقن فيال', '450', NULL, 'Antiviral.interferon beta', 'Interferon beta-1b', 'Bayer healthcare', 'Inj', 'حقن', '250 mcg', '1', NULL, NULL, NULL, NULL, 1772, '2022-10-02', 'For the treatment of venereal or genital warts caused by the Human Papiloma Virus.', 'Interferon alpha binds to type I interferon receptors (IFNAR1 and IFNAR2c) which, upon dimerization, activate two Jak (Janus kinase) tyrosine kinases (Jak1 and Tyk2). These transphosphorylate themselves and phosphorylate the receptors. The phosphorylated INFAR receptors then bind to Stat1 and Stat2 (signal transducers and activators of transcription)which dimerize and activate multiple (~100) immunomodulatory and antiviral proteins. Interferon alpha binds less stably to type I interferon receptors than interferon beta.', 'Upregulates the expression of MHC I proteins, allowing for increased presentation of peptides derived from viral antigens. This enhances the activation of CD8+ T cells that are the precursors for cytotoxic T lymphocytes (CTLs) and makes the macrophage a better target for CTL-mediated killing. Interferon alpha also induce the synthesis of several key antiviral mediators, including 2''-5'' oligoadenylate synthetase (2''-5'' A synthetase) and protein kinase R.', 'DrugBank', 0, 0, 0);
