INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (17665, 'Dilozole 20 tab.', 'ديلوزول 20 قرص', '4', NULL, 'Antiprotozoal.nitroimidazole', 'Diloxanide furoate+metronidazole', 'Alexandria', 'Tab', 'أقراص', NULL, '2', NULL, 'about diloxanide furoate dichloroacetamide derivative anti-protozoal luminal amebicides. mechanism of action of diloxanide furoate diloxanide furoate is a dicloroacetamide derivative which acts as a luminal amoebicide. it exerts its action by directly kil', '6223000172469', NULL, 1171, '2022-12-04', 'Diloxanide is used alone as a primary agent in the treatment of asymptomatic (cyst passers) intestinal amebiasis caused by Entamoeba histolytica. Diloxanide may also be used concurrently, or sequentially, with other agents such as the nitroimidazoles (eg. metronidazole) in the treatment of invasive or extraintestinal forms of amebiasis.

', 'Unknown. Diloxanide may inhibit protein synthesis. ', 'Diloxanide is a luminal amebicide, however the mechanism of action of diloxanide is unknown. Diloxanide destroys the trophozoites of E. histolytica that eventually form into cysts. The cysts are then excreted by persons infected with asymptomatic amebiasis. Diloxanide furoate is a prodrug, and is hydrolyzed in the gastrointestinal tract to produce diloxanide, the active ingredient. ', 'DrugBank', 0, 0, 0);
