INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (8001, 'Mepafuran 100 mg 20 caps.', 'ميبافيوران 100مجم 20 كبسول', '54', '37', 'Uti infections', 'Nitrofurantoin', 'Mepaco', 'Cap', 'كبسولة', '100 mg', '2', 'علاج التهابات الجهاز البولي -- تقليل الالام اثناء التبول --تقليل الحصوات', 'about nitrofurantoin nitrofuran derivative antibiotic urinary germicide miscellaneous antibiotic. mechanism of action of nitrofurantoin nitrofurantoin is a bacteriostatic drug. it undergoes enzymatic reduction and produces active metabolite which damages', '6223002211081', NULL, 23623, '2024-09-07', 'Nitrofurantoin is indicated to treat acute uncomplicated urinary tract infections.[L6856,L6859,L6862]', 'Nitrofurantoin is converted by bacterial nitroreductases to electrophilic intermediates which inhibit the citric acid cycle as well as synthesis of DNA, RNA, and protein.[A179824]', 'Nitrofurantoin interferes with vital processes in bacteria, which leads to their death.[A179824] Nitrofurantoin rapidly reaches therapeutic concentrations in the urine and is also cleared rapidly.[A179830]', 'DrugBank', 0, 0, 0);
