INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13511, 'Adricin 50 mg/25ml i.v. vial', 'ادريسين 50مجم/25مل فيال وريد', '314', '218', 'Antineoplastic', 'Doxorubicin', 'Hikma specialized pharmaceuticals', 'Vial', 'فيال', '50 mg', '1', NULL, NULL, NULL, NULL, 1900, '2024-09-09', 'Doxorubicin is indicated for the treatment of neoplastic conditions like acute lymphoblastic leukemia, acute myeloblastic leukemia, Hodgkin and non-Hodgkin lymphoma, metastatic breast cancer, metastatic Wilms’ tumor, metastatic neuroblastoma, metastatic soft tissue and bone sarcomas, metastatic ovarian carcinoma, metastatic transitional cell bladder carcinoma, metastatic thyroid carcinoma, metastatic gastric carcinoma, and metastatic bronchogenic carcinoma.[L45231] Doxorubicin is also indicated for use as a component of adjuvant therapy in women with evidence of axillary lymph node involvement following resection of primary breast cancer.[L45231] For the liposomal formulation, doxorubicin is indicated for the treatment of ovarian cancer that has progressed or recurred after platinum-based chemotherapy, AIDS-Related Kaposi''s Sarcoma after the failure of prior systemic chemotherapy or intolerance to such therapy, and multiple myeloma in combination with bortezomib in patients who have not previously received bortezomib and have received at least one prior therapy.[L45226]', 'Generally, doxorubicin is thought to exert its antineoplastic activity through 2 primary mechanisms: intercalation into DNA and disrupt topoisomerase-mediated repairs and free radicals-mediated cellular damages.[A257614]



Doxorubicin can intercalate into DNA through the anthraquinone ring, which stabilizes the complex by forming hydrogen bonds with DNA bases.[A257659] Intercalation of doxorubicin can introduce torsional stress into the polynucleotide structure, thus destabilizing nucleosome structures and leading to nucleosome eviction and replacement.[A257664,A18538] Additionally, the doxorubicin-DNA complex can interfere with topoisomerase II enzyme activity by preventing relegation of topoisomerase-mediated DNA breaks, thus inhibiting replication and transcription and inducing apoptosis.[A257669,A257674] 



Moreover, doxorubicin can be metabolized by microsomal NADPH-cytochrome P-450 reductase into a semiquinone radical, which can be reoxidized in the presence of oxygen to form oxygen radicals.[A1579,A257584] Reactive oxygen species have been known to cause cellular damage through various mechanisms, including lipid peroxidation and membrane damage, DNA damage, oxidative stress, and apoptosis.[A257634] Although free radicals generated from this pathway can be deactivated by catalase and superoxide dismutase, tumor and myocardial cells tend to lack these enzymes, thus explaining doxorubicin''s effectiveness against cancer cells and tendency to cause cardiotoxicity.[A1579,A257584,A1578]

', 'Doxorubicin is a cytotoxic, cell-cycle non-specific anthracycline antibiotic.[A1574,A257674] It is generally thought to exert its antitumor effect by destabilizing DNA structures through intercalation, thus introducing DNA strand breakages and damages.[A257664,A18538,A257674] Not only does it alter the transcriptomes of the cells, failure in repairing DNA structures can also initiate the apoptotic pathways.[A257674,A257679] Additionally, doxorubicin intercalation can also interfere with vital enzyme activity, such as topoisomerase II, DNA polymerase, and RNA polymerase, leading to cell cycle arrests.[A257674] Finally, doxorubicin can also generate cytotoxic reactive oxygen species to exert cellular damages.[A257584]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26899, 'Shade out cream 15 gm', 'شيد اوت كريم 15 جم', '100', '91', 'Eye contour', 'Aqua+vitamin c+vitamin e+panthenol+eutanol g+octyl methoxycinnamate+titanium dioxide+allantoin+glyce', 'Mash premiere', 'Cream', 'كريم', '15 gm', '1', 'حالات الهالات السوداء تحت العين', 'eye contour cream that nourishes the skin around the eyes', '6222001405071', NULL, 785, '2024-04-15', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15448, 'Bortezomib-accord 3.5mg powder in vial for i.v. inj.', 'بورتيزوميب اكورد باودر 3.5 مجم فيال', '4,745', NULL, 'Antineoplastic', 'Bortezomib', 'Accord healthcare limited > united group pharmaceutical(u.g.p.)', 'Inj', 'حقن', '3.5mg', '1', NULL, 'mechanism of action bortezomib is a reversible inhibitor of the chymotrypsin-like activity of the 26s proteasome in mammalian cells. the 26s proteasome is a large protein complex that degrades ubiquitinated proteins. the active site of the proteasome has', NULL, NULL, 2385, '2022-07-13', 'Bortezomib is indicated for the treatment of adults with multiple myeloma or mantle cell lymphoma.[L44341]', 'The ubiquitin-proteasome pathway is a homeostatic proteolytic pathway for intracellular protein degradation: proteins marked with a poly-ubiquitin chain are degraded to small peptides and free ubiquitin by the proteasome, which is a large multimeric protease.[A18508] Aberrant proteasome-dependent proteolysis, as seen in some malignancies, can lead to uncontrolled cell division, leading to tumorigenesis, cancer growth, and spread.[A18508, A204083] 



Bortezomib is a reversible inhibitor of the 26S proteasome, which is made up of a 20S core complexed with a 19S regulatory complex. Individual β-subunits allow specific catalytic action of the 20S core.[A18509, A204083] In mammalian cells, bortezomib is a potent inhibitor of the proteasome’s chymotryptic-like activity, which is attributed to the β5-subunit of the 20S core particle.[A18509] Bortezomib binds to the active site of the threonine hydroxyl group in the β5-subunit.[A204083] A probing study showed bortezomib also binding to and inhibiting the β1-subunit, which mediates the caspase-like activity of the proteasome, and β1i-subunit, which is an altered subunit that is expressed to form immunoproteasomes in response to cell stress or inflammation.[A18509] By inhibiting the proteasome-mediated degradation of key proteins that promote cell apoptosis,[A18508] bortezomib induces a cell cycle arrest during the G2-M phase.[L14180] It is believed that multiple mechanisms, other than proteasome inhibition, may be involved in the anticancer activity of bortezomib.[A204083] The anticancer activity of bortezomib was largely associated with suppression of the NF-κB signalling pathway, resulting in the downregulation of anti-apoptotic target genes and expression of anti-apoptic proteins. This may be explained by bortezomib preventing uncontrolled degradation of IκB, which is an inhibitory protein of NF-κB. NOXA, which is a pro-apoptotic factor, induced by bortezomib selectively in cancer cells; thus, it is suggested to be another key mechanism of bortezomib.[A204083]', 'Bortezomib works to target the ubiquitin-proteasome pathway, an essential molecular pathway that regulates intracellular concentrations of proteins and promotes protein degradation.[A272] The ubiquitin-proteasome pathway is often dysregulated in pathological conditions, leading to aberrant pathway signalling and the formation of malignant cells. In one study, patient-derived chronic lymphocytic leukemia (CLL) cells contained 3-fold higher levels of chymotrypsin-like proteasome activity than normal lymphocytes.[A272] By reversibly inhibiting proteasome, bortezomib prevents proteasome-mediated proteolysis. Bortezomib exerts a cytotoxic effect on various cancer cell types _in vitro_ and delays tumour growth _in vivo_ in nonclinical tumour models.[L14177] Bortezomib inhibits the proteasome activity in a dose-dependent manner. In one pharmacodynamic study, more than 75% of proteasome inhibition was observed in whole blood samples within one hour after dosing of bortezomib.[A204083]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14875, 'Bacticlor 500 mg 12caps.', 'بكتيكلور 500 مجم 12 كبسولة', '33', NULL, 'Antibiotic.cephalosporin.second-generation', 'Cefaclor', 'Pharco b > ranbaxy', 'Cap', 'كبسولة', '500 mg', '2', NULL, 'about cefaclor a second-generation cephalosporin antibiotic oral broad-spectrum antibiotics. mechanism of action cefaclor is a 2nd generation cephalosporin which exerts its bactericidal action against both gram positive & gram negative organisms by inhibi', NULL, NULL, 1513, '2022-07-24', 'For the treatment of certain infections caused by bacteria such as pneumonia and ear, lung, skin, throat, and urinary tract infections.', 'Cefaclor, like the penicillins, is a beta-lactam antibiotic. By binding to specific penicillin-binding proteins (PBPs) located inside the bacterial cell wall, it inhibits the third and last stage of bacterial cell wall synthesis. Cell lysis is then mediated by bacterial cell wall autolytic enzymes such as autolysins. It is possible that cefaclor interferes with an autolysin inhibitor.', 'Cefaclor is a second generation cephalosporin antibiotic with a spectrum resembling first-generation cephalosporins. <i>In vitro</i> tests demonstrate that the bactericidal action of the cephalosporins results from inhibition of cell-wall synthesis. As indicated by _in vitro_ and _in vivo_ clinical studies, cefaclor was shown to be effective against most strains of Gram positive aerobes - Staphylococci (including coagulase-positive, coagulase-negative, and penicillinase-producing strains), <i>Streptococcus pneumoniae</i>, <i>Streptococcus pyogenes</i> (group A &szlig;-hemolytic streptococci), as well as Gram-negative aerobes - <i>Escherichia coli</i>, <i>Haemophilus influenzae</i> (including &szlig;-lactamase-producing ampicillin-resistant strains), <i>Klebsiella sp</i>, and <i>Proteus mirabilis</i>.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (21484, 'Konda hair oil spray 120 ml', 'كوندا سبراي زيت شعر 120 مل', '230', '180', 'Hair care', 'Olive oil+seasame oil+blackseed oil+..', 'As pharma', 'Spray', 'بخاخ', '120 ml', '1', NULL, 'restores hair health from root to ends', NULL, NULL, 560, '2024-07-27', 'In a clinical context, olive oil is used as a source of calories and fatty acids in patients requiring parenteral nutrition. A formulation called Clinolipid, comprising olive oil and [soybean oil], is indicated in all age groups, including term and preterm neonates, as a source of calories and essential fatty acids for parenteral nutrition (PN) when oral or enteral nutrition is not possible, insufficient, or contraindicated.[L51013]', 'Fatty acids act as a substrate in energy production through beta-oxidation as well as important components of cell membrane structures and prescursors for bioactive molecules like prostaglandins.', 'Provides a source of calories and fatty acids.[L51013]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (646, 'Amoxycillin 500mg 12 caps b.p.2013', 'اموكسيسللين 500مجم 12 كبسول', '9', NULL, 'Penicillins.monocomponent', 'Amoxicillin', 'Arab drug company.', 'Cap', 'كبسولة', '500mg', '2', NULL, '(indications): is a penicillin-like beta-lactam antibiotic. it is used to treat bacterial infections caused by susceptible microorganisms. it prevents the bacterial growth and is used in the treatment of a number of infections such as pneumonia bronchitis', '6221060000463', NULL, 1399, '2022-12-01', 'Amoxicillin alone is indicated to treat susceptible bacterial infections of the ear, nose, throat, genitourinary tract, skin, skin structure, and lower respiratory tract.[L11656,L11659] Amoxicillin is given with calvulanic acid to treat acute bacterial sinusitis, community acquired pneumonia, lower respiratory tract infections, acute bacterial otitis media, skin and skin structure infections, and urinary tract infections.[L11650,L7880,L11653] Amoxicillin is given with omeprazole in the treatment of _Helicobacter pylori_ (_H. pylori_) infection.[L9743,L11647]



Amoxicillin is used in combination with [vonoprazan] and [clarithromycin] as co-packaged triple therapy or in combination with [vonoprazan] as co-packaged dual therapy to treat _H. pylori_ infection in adults.[L41695]', 'Amoxicillin competitively inhibits penicillin-binding protein 1 and other high molecular weight penicillin binding proteins.[A6205,A190678]  Penicillin bind proteins are responsible for glycosyltransferase and transpeptidase reactions that lead to cross-linking of D-alanine and D-aspartic acid in bacterial cell walls.[A190678] Without the action of penicillin binding proteins, bacteria upregulate autolytic enzymes and are unable to build and repair the cell wall, leading to bacteriocidal action.[A190642,A190678]', 'Amoxicillin competitively inhibit penicillin binding proteins, leading to upregulation of autolytic enzymes and inhibition of cell wall synthesis.[A6205,A190678,A190642] Amoxicillin has a long duration of action as it is usually given twice daily.[L7880] Amoxicillin has a wide therapeutic range as mild overdoses are not associated with significant toxicity.[L11650] Patients should be counselled regarding the risk of anaphylaxis, _Clostridium difficile_ infections, and bacterial resistance.[L11656]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20497, 'Unislim 30 tablets', 'يونيسليم 30 قرص', '400', NULL, 'Weight control', 'Natural ingredients', 'Unipharma', 'Tablet', 'أقراص', NULL, '3', 'لانقاص الوزن', NULL, NULL, NULL, 2030, '2023-01-25', NULL, 'The predominant targets of NK cells are ‘stressed’ cells with downregulated expression of class I MHC (MHC-I), a common mechanism used by malignant or virus-infected cells to evade recognition by the body’s immune system. In response to encountering these cells, NK cells release two types of proteins, perforins and granzyme B, which attack the target cell’s membrane, damage its organelles, and trigger apoptosis. NK Cell immunotherapy is an emerging anti-cancer strategy, as these cells are able to exert effects without activating the graft-versus-host disease cascade. In addition, their broad mechanism allows for generally safe administration of NK cells that are not immunologically matched to a patient. ', NULL, 'DrugBank', 0, 0, 0);
