INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (19070, 'Flabu 600 mg 20 f.c. tabs.', 'فلابو 600 مجم 20 قرص', '10', NULL, 'Nsaid.propionic acid derivatives', 'Ibuprofen', 'Delta pharma', 'Tab', 'أقراص', '600 mg', '2', 'مسكن عام -- خافض للحرارة -- تقليل الالام', 'about ibuprofen nsaid a propionic acid derivative analgesic and anti-inflammatory. mechanism of action of ibuprofen ibuprofen has analgesic anti-inflammatory and antipyretic action. it acts by inhibiting prostaglandin (pgs) synthesis and their release at', '6221163007604', NULL, 874, '2022-12-05', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Ibuprofen is a non-steroidal anti-inflammatory drug (NSAID) derived from propionic acid and it is considered the first of the propionics.[A39074] The formula of ibuprofen is 2-(4-isobutylphenyl) propionic acid and its initial development was in 1960 while researching for a safer alternative for aspirin.[A39075] Ibuprofen was finally patented in 1961 and this drug was first launched against rheumatoid arthritis in the UK in 1969 and USA in 1974. It was the first available over-the-counter NSAID.[A39076]

On the available products, ibuprofen is administered as a racemic mixture. Once administered, the R-enantiomer undergoes extensive interconversion to the S-enantiomer _in vivo_ by the activity of the alpha-methylacyl-CoA racemase. In particular, it is generally proposed that the S-enantiomer is capable of eliciting stronger pharmacological activity than the R-enantiomer.[A39194]', '["G02CC01", "C01EB16", "M01AE01", "M02AA13", "R02AX02", "N02AJ08", "N02AJ19", "M01AE51"]', '[]');
