INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (21238, 'Kansartan 75mg 30 tab.', 'كانزارتان 75مجم 30 قرص', '33', NULL, 'Antihypertensive.angiotensin blocker', 'Irbesartan', 'Hikma pharma > chemipharm', 'Tab', 'أقراص', '75mg', '3', NULL, 'about irbesartan angiotensin ii receptor blocker a non-peptide compound antihypertensive. mechanism of action of irbesartan it is a competitive antagonist of angiotensin-2 at at-1 receptor. it decreases peripheral resistance and lowers bp in hypertensive', NULL, NULL, 790, '2023-01-05', 0, 'Irbesartan is indicated to treat hypertension and diabetic nephropathy in hypertensive patients with type 2 diabetes, elevated serum creatinine, and proteinuria.[L7456] A combination product with hydrochlorothiazide is indicated for hypertension in patients with uncontrolled hypertension with monotherapy or first line in patients not expected to be well controlled with monotherapy.[L7459]', 'Irbesartan prevents angiotensin II binding to the AT<sub>1</sub> receptor in tissues like vascular smooth muscle and the adrenal gland.[L7456,L7459] Irbesartan and its active metabolite bind the AT<sub>1</sub> receptor with 8500 times more affinity than they bind to the AT<sub>2</sub> receptor.[L7456,L7459] Irbesartan''s prevention of angiotensin II binding causes vascular smooth muscle relaxation and prevents the secretion of aldosterone, lowering blood pressure.[L7456,L7459]



Angiotensin II would otherwise bind to the AT<sub>1</sub> receptor, inducing vasoconstriction and aldosterone secretion, raising blood pressure.[L7456,L7459]', 'Irbesartan is an angiotensin receptor blocker used to treat hypertension and diabetic nephropathy.[L7456,L7459] It has a long duration of action as it is usually taken once daily and a wide therapeutic index as doses may be as low as 150mg daily but doses of 900mg/day were well tolerated in healthy human subjects.[L7456,L7459,A181165]', 'DrugBank', 0, 0, 0);
