INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (21325, 'Kenara mouth wash 250 ml', 'كينارا غسول للفم 250 مل', '25', NULL, 'Oral care', 'Thymol+menthol crystals+chlorhexidine digluconate+glycerin', 'Macro group pharmaceuticals', 'Mouth wash', 'غسول للفم', '250 ml', '1', NULL, '*unique formula has the efficacy of thyme extract & ph buffering system for a healthier mouth and teeth. *sugar free & sls free mouthwash for optimum safety with care *relieves halitosis & xerostomia (dry mouth) by marvelous combination of oral antimicrob', NULL, NULL, 907, '2022-07-15', 0, NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (21326, 'Kenzo 2% shampoo 60 ml', 'كينزو 2% شامبو 60 مل', '28', NULL, 'Antifungals.imidazoles', 'Ketoconazole', 'Borg', 'Amp', 'أمبول', '2%', '1', 'شامبو مضاد للفطريات الشعر -- علاج القشرة في فروة الرأس -- علاج الامراض الجلدية لفروة الرأس', 'about ketoconazole imidazole derivative a broad spectrum antifungal. mechanism of action of ketoconazole ketoconazole is fungicidal or fungistatic depending on concentrations. i t inhibits the conversion of lanosterol to 14 demethyl lanosterol by inhibiti', '6223002570966', NULL, 8295, '2023-02-03', 0, 'Ketoconazole is used in the treatment or prevention of fungal infections including blastomycosis, candidiasis, coccidioidomycosis, histoplasmosis, chromomycosis, and paracoccidioidomycosis.[FDA Label] In Europe, it is also used in the treatment of endogenous Cushing''s syndrome.[L7736]', 'Ketoconazole interacts with 14-&alpha;-sterol demethylase, a cytochrome P-450 enzyme necessary for the conversion of lanosterol to ergosterol.[A181802,T116] This results in inhibition of ergosterol synthesis and increased fungal cellular permeability due to reduced amounts of ergosterol present in the fungal cell membrane. This metabolic inhibition also results in accumulation of 14α-methyl-3,6-diol, a toxic metabolite. The increase in membrane fluidity is also thought to produce impairment of membrane-bound enzyme systems as components become less closely packed.', 'Ketoconazole, similarly to other azole antifungals, is a fungistatic agent which causes growth arrest in fungal cells thereby preventing growth and spread of the fungus throughout the body.[T116]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (21328, 'Kepilepsy 10gm/100ml oral soln. 120 ml', 'كيبيلبسي 10 جم / 100 مل محلول 120 مل', '48', NULL, 'Anti-epileptic.pyrrolidine', 'Levetiracetam', 'Chemipharm', 'Sol', 'محلول', '10gm', '1', NULL, 'about levetiracetam it is s-enantiomer of etiracetam. anticonvulsant medication used to treat epilepsy. mechanism of action of levetiracetam the exact mechanism for levetiracetam is unknown. however the drug binds to a synaptic vesicle protein sv2a which', NULL, NULL, 748, '2022-07-15', 0, 'Levetiracetam is indicated as an adjunctive therapy in the treatment of partial onset seizures in epileptic patients who are one month of age and older. Additionally, it is indicated as an adjunct in the treatment of myoclonic seizures in patients with juvenile myoclonic epilepsy who are 12 years of age and older, and in primary generalized tonic-clonic seizures in patients with idiopathic generalized epilepsy who are 6 years of age and older.[L8606]



Levetiracetam is also available as an orally dissolvable tablet that is indicated as an adjunct in the treatment of partial onset seizures in patients with epilepsy who are 4 years of age and older and weigh more than 20kg.[L8609]', 'The exact mechanism through which levetiracetam exerts its anti-epileptic effects is unclear, but is thought to be unique amongst other anti-epileptic medications. Current knowledge suggests that levetiracetam’s binding to synaptic vesicle protein 2A (SV2A) is a key driver of its action. SV2A is a membrane-bound protein that is found on synaptic vesicles and is ubiquitous throughout the CNS[A12546] - it appears to play a role in vesicle exocytosis[L8606,L8615] and in the modulation of synaptic transmission by increasing the available amount of secretory vesicles available for neurotransmission.[A12552] Stimulation of pre-synaptic SV2A by levetiracetam may inhibit neurotransmitter release,[A12551] but this action does not appear to affect normal neurotransmission. This has led to the suggestion that levetiracetam exclusively modulates the function of SV2A only under pathophysiological conditions.[A12546] Levetiracetam and related analogues showed a correlation between affinity for SV2A and anti-epileptic potency, further suggesting that action at this site contributes to the anti-epileptic activity of the drug.[L8606,L8615]



Levetiracetam has also been shown to indirectly affect GABAergic neurotransmission (despite having no direct effect on GABAergic or glutamatergic receptors) and modulate ionic currents.[A12552] Similarly, levetiracetam has been shown in vitro to inhibit N-type calcium channels.[A16562] How, or even if, these actions are implicated in its anti-epileptic action have yet to be elucidated.', 'Levetiracetam appears to prevent seizure activity via the selective inhibition of hypersynchronized epileptiform burst firing without affecting normal neuronal transmission, though the exact mechanism through which this occurs is unclear.[L8606,L8615] The therapeutic index of levetiracetam is wide,[L8615,A185918] making it relatively unique amongst other anti-epileptic medications.



Anti-epileptic drugs, including levetiracetam, may increase the risk of suicidal ideation or behaviour - patients taking levetiracetam should be monitored for the emergence or worsening of depressive symptoms, suicidal ideation, and behavioural abnormalities.[L8606,L8600,L8615]', 'DrugBank', 0, 0, 0);
