INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28257, 'Tolevo m.r. 4 mg 30 caps.', 'توليفو ام ار 4مجم 30 كبسولة', '60', NULL, 'Urinary incontinence.antispasmodics.muscarinic antagonists', 'Tolterodine l tartarate', 'Rameda', 'Cap', 'كبسولة', '4 mg', '3', NULL, 'about tolterodine tartarate anticholinergic ( antimuscarinic) treat urinary incontinence. mechanism of action of tolterodine tartarate tolterodine tartrate is a specific muscarinic receptor (m3 receptor) antagonist. it blocks muscarinic receptors which ca', NULL, NULL, 1023, '2022-07-09', 'For the treatment of overactive bladder (with symptoms of urinary frequency, urgency, or urge incontinence).', 'Both tolterodine and its active metabolite, 5-hydroxymethyltolterodine, act as competitive antagonists at muscarinic receptors. This antagonism results in inhibition of bladder contraction, decrease in detrusor pressure, and an incomplete emptying of the bladder.', 'Tolterodine is a competitive muscarinic receptor antagonist. Both urinary bladder contraction and salivation are mediated via cholinergic muscarinic receptors. After oral administration, tolterodine is metabolized in the liver, resulting in the formation of the 5-hydroxymethyl derivative, a major pharmacologically active metabolite. The 5-hydroxymethyl metabolite, which exhibits an antimuscarinic activity similar to that of tolterodine, contributes significantly to the therapeutic effect. Both tolterodine and the 5-hydroxymethyl metabolite exhibit a high specificity for muscarinic receptors, since both show negligible activity or affinity for other neurotransmitter receptors and other potential cellular targets, such as calcium channels. Tolterodine has a pronounced effect on bladder function. The main effects of tolterodine are an increase in residual urine, reflecting an incomplete emptying of the bladder, and a decrease in detrusor pressure, consistent with an antimuscarinic action on the lower urinary tract.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4669, 'Actiflexin 30 caps.', 'اكتيفلكسين 30 كبسولة', '210', NULL, 'Joint health', 'Natural eggshell membrane', 'Pharma medica', 'Cap', 'كبسولة', NULL, '3', NULL, NULL, NULL, NULL, 733, '2023-05-01', NULL, 'The predominant targets of NK cells are ‘stressed’ cells with downregulated expression of class I MHC (MHC-I), a common mechanism used by malignant or virus-infected cells to evade recognition by the body’s immune system. In response to encountering these cells, NK cells release two types of proteins, perforins and granzyme B, which attack the target cell’s membrane, damage its organelles, and trigger apoptosis. NK Cell immunotherapy is an emerging anti-cancer strategy, as these cells are able to exert effects without activating the graft-versus-host disease cascade. In addition, their broad mechanism allows for generally safe administration of NK cells that are not immunologically matched to a patient. ', NULL, 'DrugBank', 0, 0, 0);
