INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26069, 'Ricfalin cream 50 gm', 'ريكفالين كريم 50جم', '33', NULL, 'Massage', 'Natural products', 'Crab sinergy > biomic', 'Cream', 'كريم', '50 gm', '1', NULL, NULL, NULL, NULL, 505, '2022-08-04', NULL, 'The predominant targets of NK cells are ‘stressed’ cells with downregulated expression of class I MHC (MHC-I), a common mechanism used by malignant or virus-infected cells to evade recognition by the body’s immune system. In response to encountering these cells, NK cells release two types of proteins, perforins and granzyme B, which attack the target cell’s membrane, damage its organelles, and trigger apoptosis. NK Cell immunotherapy is an emerging anti-cancer strategy, as these cells are able to exert effects without activating the graft-versus-host disease cascade. In addition, their broad mechanism allows for generally safe administration of NK cells that are not immunologically matched to a patient. ', NULL, 'DrugBank', 0, 0, 0);
