INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22003, 'Lomex 400 mg 10 tab.', 'لوميكس 400مجم 10 اقراص', '30', NULL, 'Antibiotic.quinolone', 'Lomefloxacin', 'Sigma', 'Tab', 'أقراص', '400 mg', '1', NULL, 'about lomefloxacin a second generation fluoroquinolone antibiotic antibiotic. mechanism of action of lomefloxacin lomefloxacin is a bactericidal drug. it act by inhibiting the enzyme dna gyrase (topoisomerase 2) and topoisomerase 4.dna gyrase helps in the', '6221051010112', NULL, 769, '2023-01-11', 0, 'For the treatment of bacterial infections of the respiratory tract (chronic bronchitis) and urinary tract, and as a pre-operative prophylactic to prevent urinary tract infection caused by: <i>S.pneumoniae</i>, <i>H.influenzae</i>, <i>S.aureus</i>, <i>P.aeruginosa</i>, <i>E. cloacae</i>, <i>P. mirabilis</i>, <i>C. civersus</i>, <i>S. asprphyticus</i>, <i>E.coli</i>, and <i>K.pneumoniae</i>.', 'Lomefloxacin is a bactericidal fluoroquinolone agent with activity against a wide range of gram-negative and gram-positive organisms. The bactericidal action of lomefloxacin results from interference with the activity of the bacterial enzymes DNA gyrase and topoisomerase IV, which are needed for the transcription and replication of bacterial DNA. DNA gyrase appears to be the primary quinolone target for gram-negative bacteria. Topoisomerase IV appears to be the preferential target in gram-positive organisms. Interference with these two topoisomerases results in strand breakage of the bacterial chromosome, supercoiling, and resealing. As a result DNA replication and transcription is inhibited.', 'Lomefloxacin is a fluoroquinolone antibiotic used to treat chronic bronchitis, as well as complicated and uncomplicated urinary tract infections. It is also used as a prophylactic or preventative treatment to prevent urinary tract infections in patients undergoing transrectal or transurethral surgical procedures. Flouroquinolones such as lomefloxacin possess excellent activity against gram-negative aerobic bacteria such as <i>E.coli</i> and <i>Neisseria gonorrhoea</i> as well as gram-positive bacteria including <i>S. pneumoniae</i> and <i>Staphylococcus aureus</i>. They also posses effective activity against shigella, salmonella, campylobacter, gonococcal organisms, and multi drug resistant pseudomonas and enterobacter.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22004, 'Lomexcure 3mg/ml eye drops 5 ml', 'لوميكسكور 3 مجم / مل قطرة 5 مل', '9', NULL, 'Antibiotic.quinolone', 'Lomefloxacin', 'Pharco', 'Drops', 'نقط', '3mg', '1', NULL, 'about lomefloxacin a second generation fluoroquinolone antibiotic antibiotic. mechanism of action of lomefloxacin lomefloxacin is a bactericidal drug. it act by inhibiting the enzyme dna gyrase (topoisomerase 2) and topoisomerase 4.dna gyrase helps in the', '6221151002895', NULL, 697, '2022-07-25', 0, 'For the treatment of bacterial infections of the respiratory tract (chronic bronchitis) and urinary tract, and as a pre-operative prophylactic to prevent urinary tract infection caused by: <i>S.pneumoniae</i>, <i>H.influenzae</i>, <i>S.aureus</i>, <i>P.aeruginosa</i>, <i>E. cloacae</i>, <i>P. mirabilis</i>, <i>C. civersus</i>, <i>S. asprphyticus</i>, <i>E.coli</i>, and <i>K.pneumoniae</i>.', 'Lomefloxacin is a bactericidal fluoroquinolone agent with activity against a wide range of gram-negative and gram-positive organisms. The bactericidal action of lomefloxacin results from interference with the activity of the bacterial enzymes DNA gyrase and topoisomerase IV, which are needed for the transcription and replication of bacterial DNA. DNA gyrase appears to be the primary quinolone target for gram-negative bacteria. Topoisomerase IV appears to be the preferential target in gram-positive organisms. Interference with these two topoisomerases results in strand breakage of the bacterial chromosome, supercoiling, and resealing. As a result DNA replication and transcription is inhibited.', 'Lomefloxacin is a fluoroquinolone antibiotic used to treat chronic bronchitis, as well as complicated and uncomplicated urinary tract infections. It is also used as a prophylactic or preventative treatment to prevent urinary tract infections in patients undergoing transrectal or transurethral surgical procedures. Flouroquinolones such as lomefloxacin possess excellent activity against gram-negative aerobic bacteria such as <i>E.coli</i> and <i>Neisseria gonorrhoea</i> as well as gram-positive bacteria including <i>S. pneumoniae</i> and <i>Staphylococcus aureus</i>. They also posses effective activity against shigella, salmonella, campylobacter, gonococcal organisms, and multi drug resistant pseudomonas and enterobacter.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22006, 'Lomustine-medac 40mg 20 caps. ss', 'لوموستين-ميداك 40مجم 20 كبسولة', '8,550', '5500', 'Antineoplastic', 'Lomustine', 'Medac > egyptian pharmaceutical trading company', 'Cap', 'كبسولة', '40mg', '2', 'علاج الاورام', NULL, NULL, NULL, 3320, '2024-03-31', 0, 'For the treatment of primary and metastatic brain tumors as a component of combination chemotherapy in addition to appropriate surgical and/or radiotherapeutic procedures. Also used in combination with other agents as secondary therapy for the treatment of refractory or relapsed Hodgkin''s disease.', 'Lomustine is a highly lipophilic nitrosourea compound which undergoes hydrolysis in vivo to form reactive metabolites. These metabolites cause alkylation and cross-linking of DNA (at the O6 position of guanine-containing bases) and RNA, thus inducing cytotoxicity. Other biologic effects include inhibition of DNA synthesis and some cell cycle phase specificity. Nitrosureas generally lack cross-resistance with other alkylating agents. As lomustine is a nitrosurea, it may also inhibit several key processes such as carbamoylation and modification of cellular proteins.', 'Lomustine is an alkylating agent of the nitrosourea type. Lomustine and its metabolites interferes with the function of DNA and RNA. It is cell cycle&ndash;phase nonspecific. Cancers form when some cells within the body multiply uncontrollably and abnormally. These cells then spread and destroy nearby tissues. Lomustine acts by slowing this process down. It kills cancer cells by damaging the DNA (the genetic material inside the cells) and stops them from dividing.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22007, 'Lomy massage cream 60 gm', 'لومي كريم مساج 60 جم', '35', NULL, 'Massage', 'Vitamin c+cucumber ext.+green tea oil+almond oil+tocopherol+eucalyptus oil+menthol+panthenol+glycerin+camphor oil', 'Hi-care > famila medifood', 'Cream', 'كريم', '60 gm', '1', NULL, NULL, NULL, NULL, 630, '2022-07-13', 0, 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22008, 'Longacef 1 gm i.v. vial', 'لونجاسيف 1 جرام فيال وريد', '83', '57.5', 'Antibiotic.cephalosporin.third-generation', 'Ceftriaxone', 'The united pharmaceuticals co ltd-jordan > modern pharma', 'Vial', 'فيال', '1 gm', '1', NULL, 'about ceftriaxone third generation cephalosporin antibiotic(parenteral). mechanism of action of ceftriaxone ceftriaxone is a 3rd generation cephalosporin which exerts its bactericidal action against both gram positive & gram negative organisms by inhibiti', '6251158630203', NULL, 939, '2024-12-29', 0, 'Ceftriaxone is used for the treatment of the infections (respiratory, skin, soft tissue, UTI, ENT) caused by susceptible organisms.[L15082] Organisms that are generally susceptible to ceftriaxone include S. pneumoniae, S. pyogenes (group A beta-hemolytic streptococci), coagulase-negative staphylococci, Some Enterobacter spp, H. influenzae, N. gonorrhoeae, P. mirabilis, E. coli, Klebsiella spp, M. catarrhalis, B. burgdorferi, and some oral anaerobes.[A215582] ', 'Ceftriaxone works by inhibiting the mucopeptide synthesis in the bacterial cell wall.[T324,L15082] The beta-lactam moiety of ceftriaxone binds to carboxypeptidases, endopeptidases, and transpeptidases in the bacterial cytoplasmic membrane. These enzymes are involved in cell-wall synthesis and cell division. Binding of ceftriaxone to these enzymes causes the enzyme to lose activity; therefore, the bacteria produce defective cell walls, causing cell death.', 'Ceftriaxone is a cephalosporin/cephamycin beta-lactam antibiotic used in the treatment of bacterial infections caused by susceptible, usually gram-positive, organisms.[L15082] Ceftriaxone has <i>in vitro</i> activity against gram-positive aerobic, gram-negative aerobic, and anaerobic bacteria.[L15087] The bactericidal activity of ceftriaxone results from the inhibition of cell wall synthesis and is mediated through ceftriaxone binding to penicillin-binding proteins (PBPs).[T324] Ceftriaxone is stable against hydrolysis by a variety of beta-lactamases, including penicillinases, and cephalosporinases and extended-spectrum beta-lactamases.[L15082] However, resistance to ceftriaxone usually occurs through beta-lactamase hydrolysis, altered PBPs, or reduced bacterial cell permeability.[T324] Ceftriaxone should not be mixed with or giving in the same IV line as diluents/products containing calcium as they may cause ceftriaxone to precipitate.[L15082] Ceftriaxone use may also cause biliary sludge or gallbladder pseudolithiasis.[L15082,A215602] ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22010, 'Longavit b12 1mg/ml 3 amp. for im inj', 'لونجافيت ب12 1مجم/مل 3 امبولات', '16', NULL, 'Vitamin b', 'Hydroxocobalamin', 'Memphis', 'Inj', 'حقن', '1mg', '3', 'آلام العمود الفقري.', NULL, '6221050010953', NULL, 2558, '2022-04-19', 0, 'For treatment of pernicious anemia and the prevention and treatment of vitamin B12 deficiency arising from alcoholism, malabsorption, tapeworm infestation, celiac, hyperthyroidism, hepatic-biliary tract disease, persistent diarrhea, ileal resection, pancreatic cancer, renal disease, prolonged stress, vegan diets, macrobiotic diets or other restrictive diets. Also for the treatment of known or suspected cyanide poisoning.', 'Vitamin B12 exists in four major forms referred to collectively as cobalamins; deoxyadenosylcobalamin, methylcobalamin, hydroxocobalamin, and cyanocobalamin. Two of these, methylcobalamin and 5-deoxyadenosyl cobalamin, are primarily used by the body. Methionine synthase needs methylcobalamin as a cofactor. This enzyme is involved in the conversion of the amino acid homocysteine into methionine. Methionine in turn is required for DNA methylation. 5-Deoxyadenosyl cobalamin is a cofactor needed by the enzyme that converts L-methylmalonyl-CoA to succinyl-CoA. This conversion is an important step in the extraction of energy from proteins and fats. Furthermore, succinyl CoA is necessary for the production of hemoglobin, the substances that carries oxygen in red blood cells.', 'Hydroxocobalamin is a synthetic, injectable form of Vitamin B12. Hydroxocobalamin is actually a precursor of two cofactors or vitamins (Vitamin B12 and Methylcobalamin) which are involved in various biological systems in man. Vitamin B12 is required for the conversion of methylmalonate to succinate. Deficiency of this enzyme could therefore interfere with the production of lipoprotein in myelin sheath tissue and so give rise to neurological lesions. The second cofactor, Methylcobalamin, is necessary for the conversion of homocysteine to methionine which is essential for the metabolism of folic acid. Deficiency of tetrahydrafolate leads to reduced synthesis of thymidylate resulting in reduced synthesis of DNA which is essential for cell maturation. Vitamin B12 is also concerned in the maintenance of sulphydryl groups in reduced form, deficiency leading to decreased amounts of reduced SH content of erythrocytes and liver cells. Overall, vitamin B12 acts as a coenzyme for various metabolic functions, including fat and carbohydrate metabolism and protein synthesis. It is necessary for growth, cell replication, hematopoiesis, and nucleoprotein as well as myelin synthesis. This is largely due to its effects on metabolism of methionine folic acid, and malonic acid.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22012, 'Longicef 250mg/5ml susp. 60ml', 'لونجسيف 250مجم/5مل معلق 60 مل', '10', NULL, 'Antibiotic.cephalosporin.first-generation', 'Cefadroxil', 'Kahira', 'Susp', 'معلق', '250mg', '1', NULL, 'about cefadroxil first generation cephalosporin para-hydroxy derivative of cefalexin oral broad-spectrum antibiotic mechanism of action cefadroxil is a 1st generation cephalosporin which exerts its bactericidal action against both gram positive & gram neg', '6221068201909', NULL, 770, '2023-04-18', 0, 'For the treatment of the following infections (skin, UTI, ENT) caused by; <i>S. pneumoniae, H. influenzae, staphylococci, S. pyogenes</i> (group A beta-hemolytic streptococci), <i>E. coli, P. mirabilis, Klebsiella</i> sp, coagulase-negative staphylococci and <i>Streptococcus pyogenes</i>', 'Like all beta-lactam antibiotics, cefadroxil binds to specific penicillin-binding proteins (PBPs) located inside the bacterial cell wall, causing the inhibition of the third and last stage of bacterial cell wall synthesis. Cell lysis is then mediated by bacterial cell wall autolytic enzymes such as autolysins; it is possible that cefadroxil interferes with an autolysin inhibitor.', 'Cefadroxil, a first-generation cephalosporin antibiotic, is used to treat urinary tract infections, skin and skin structure infections, pharyngitis, and tonsillitis.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22014, 'Lonsurf 15/6.14 mg 20 f.c. tabs.', 'لونسيرف 15/6.14مجم 20 قرص', '21,510', '14000', 'Antineoplastic', 'Trifluridine+tipiracil', 'Taiho pharmaceutical co. ltd. > servier', 'Tab', 'أقراص', '6.14 mg', '2', NULL, 'lonsurf is indicated for the treatment of adult patients with metastatic gastric or gastroesophageal junction adenocarcinoma previously treated with at least two prior lines of chemotherapy that included a fluoropyrimidine a platinum either a taxane or ir', NULL, NULL, 1607, '2024-09-25', 0, NULL, NULL, NULL, NULL, 0, 0, 0);
