INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22405, 'Maxispect 100mg/5ml oral susp. 100 ml', 'ماكسيسبيكت 100مجم/5مل شراب معلق 100مل', '40', NULL, 'Antibiotic.cephalosporin.third-generation', 'Cefpodoxime', 'Eva pharma', 'Susp', 'معلق', '100mg', '1', NULL, 'about cefpodoxime third generation cephalosporin antibiotic oral antibiotic. mechanism of action of cefpodoxime cefpodoxime is a 3rd generation cephalosporin which exerts its bactericidal action mainly against gram positive organisms by inhibiting bacteri', NULL, NULL, 1209, '2022-07-13', 0, 'Indicated for the treatment of patients with mild to moderate infections caused by susceptible strains of the designated microorganisms.', 'Cefpodoxime is active against a wide spectrum of Gram-positive and Gram-negative bacteria. Cefpodoxime is stable in the presence of beta-lactamase enzymes. As a result, many organisms resistant to penicillins and cephalosporins, due to their production of beta-lactamase, may be susceptible to cefpodoxime. Cefpodoxime is inactivated by certain extended spectrum beta-lactamases. The bactericidal activity of cefpodoxime results from its inhibition of cell wall synthesis. The active metabolite of cefpodoxime binds preferentially to penicillin binding protein 3, which inhibits production of peptidoglycan, the primary constituent of bacterial cell walls.', 'Cefpodoxime is shown to be effective against most Gram positive and Gram negative bacteria, except <i>Pseudomonas aeruginosa</i>, <i>Enterococcus</i>, and <i>Bacteroides fragilis</i>.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22407, 'Maxitrol eye oint. 3.5 gm', 'ماكسيترول مرهم عين 3.5جم', '13', NULL, NULL, 'Dexamethasone+neomycin sulphate+polymyxin b sulphate', 'Alcon > novartis scientific office', 'Oint', 'مرهم', '3.5 gm', '1', NULL, 'about dexamethasone a potent synthetic glucocorticoid antiasthmatic anti-inflammatory and immunosuppressant. about neomycin an aminoglycoside antibiotic ammonium detoxicant. mechanism of action of neomycin this aminoglycoside cause protein synthesis inhib', '5413895006319', NULL, 1240, '2022-07-16', 0, NULL, NULL, NULL, NULL, 0, 0, 0);
