INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28030, 'Tenoxil 20mg 10 tab.', 'تينوكسيل 20مجم 10 اقراص', '18', '10.5', 'Nsaid.oxicam', 'Tenoxicam', 'Sigma > modern pharmacutical center', 'Tab', 'أقراص', '20mg', '1', NULL, 'mechanism of action of tenoxicam the anti-inflammatory effects of tenoxicam may result from the inhibition of the enzyme cyclooxygenase and the subsequent peripheral inhibition of prostaglandin synthesis (prostaglandins sensitize pain receptors their inhi', '6224000394882', NULL, 770, '2025-08-16', 'For the treatment of rheumatoid arthritis, osteoarthritis, backache, and pain.', 'The antiinflammatory effects of tenoxicam may result from the inhibition of the enzyme cycooxygenase and the subsequent peripheral inhibition of prostaglandin synthesis. As prostaglandins sensitize pain receptors, their inhibition accounts for the peripheral analgesic effects of tenoxicam. Antipyresis may occur by central action on the hypothalamus, resulting in peripheral dilation, increased cutaneous blood flow, and subsequent heat loss.', 'Tenoxicam, an antiinflammatory agent with analgesic and antipyretic properties, is used to treat osteoarthritis and control acute pain.', 'DrugBank', 0, 0, 0);
