INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13828, 'Alpetigo 1% top. oint. 15 gm', 'البيتيجو 1% مرهم 15 جم', '66', NULL, 'Impetigo', 'Retapamulin', 'Mash premiere', 'Oint', 'مرهم', '1%', '1', NULL, 'it s an antibiotic used in treatment of impetigo small infected lacerations abrasions and wounds that have sutured. use: twice daily for five days. retapamulin is an antibiotic belonging to the pleuromutilin', NULL, NULL, 777, '2022-07-14', 'For use in adults and pediatric patients aged 9 months and older for the topical treatment of impetigo (up to 100 cm<sup>2</sup> in total area in adults or 2% total body surface area in pediatric patients aged 9 months or older) due to Staphylococcus aureus (methicillin-susceptible isolates only) or Streptococcus pyogenes.', 'Retapamulin is a bacterial protein synthesis inhibitor belonging to a class of compounds called pleuromutilins. These compounds inhibit the initiation of protein synthesis by binding to a specific site on the 50S subunit of bacterial ribosome (domain V of 23S rRNA). This binding site involves ribosomal protein L3 and is in the region of the ribosomal P site and peptidyl transferase center. By virtue of binding to this site, pleuromutilins inhibit peptidyl transfer, block P-site interactions, and prevent the normal formation of active 50S ribosomal subunits.', 'Retapamulin is a semisynthetic pleuromutilin antibiotic. This drug is usually bacteriostatic in action, but may become bactericidal at highed concentrations (when MBC is 1000 times higher than MIC). Retapamulin acts by selectively inhibiting the initiation of protein synthesis in bacteria at the level of bacterial 50S ribosome.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19457, 'Rowsta 200 mg 10sachets 20 gm', 'روستا 200 مجم 10 اكياس 20 جم', '120', '85', 'Protein', 'Sweet potato+isolated whey protein', 'Unitrade pharma', 'Sachet', 'أكياس', '200 mg', '1', 'مكمل غذائى يدعم وظائف الجسم المختلفة بدءاً من وظائف الدماغ وانتهاء بجهاز المناعة وقدرته على محاربة العدوى,', NULL, NULL, NULL, 2193, '2024-02-27', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (6142, 'Medipha b12 1000mig 30 odf', 'ميديفا ب12 - 1000 مجم 30 قرص', '135', NULL, 'Vitamin b', 'Vitamin b12+folic acid (methyl folat)', 'Medipha pharma', 'Unknown', 'غير محدد', NULL, '3', NULL, NULL, NULL, NULL, 817, '2025-07-25', 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (10972, 'Sulfax magnesium cream 120 gm', 'سولفاكس مغنسيوم كريم 120 جرام', '170', '150', 'Massage cream', 'Eucalyptus oil+menthol+camphor oil+cetyl myristoleate', 'Parkville', 'Cream', 'كريم', '120 gm', '1', NULL, 'muscle care', '6224010337732', NULL, 2189, '2025-05-20', 'As an active agent, eucalyptus oil has been indicated for relief of the symptoms of catarrhal colds, and/or the relief of the symptoms of minor muscular sprains and cramps [L1857].', 'The general consensus is that the exact mechanism of action of eucalyptus oil is largely unknown at this time but comprises various hypotheses from various studies.



Cineol containing preparations of eucalyptus oil may contain up to 80% (or more) 1,8-cineole [A32243] and is one of the most common types of eucalyptus oil formulations used. As an active agent indicated for relieving certain cold symptoms and/or certain muscular sprains and cramps, it is believed that eucalyptus oil may possess some antimicrobial and anti-inflammatory activities.



Some in vitro studies of human blood monocytes suggest a dose-dependent effect of eucalyptus oil to elicit significant inhibition of multiple cytokines, perhaps in the treatment of airway inflammation [A32244, A32245]. Moreover, other studies in animal models discuss the possibility of eucalyptus oil demonstrating anti-inflammatory and anti-nociceptive effects that potentially account for inhibiting the formation of prostaglandins and cytokines by stimulated monocytes in vitro [A32246, A32247].



Furthermore, additional studies have observed eucalyptus oil anti-viral activity against herpes simplex virus (HSV-1, HSV-2) in cell cultures as well as the demonstration of broad antimicrobial activity of eucalyptus medicinal plant extracts against Alicyclobacillus acidoterretris, Bacillus cereus, E. coli, Enterococcus faecalis, MRSA, Propionibacterium acnes, S. aureus, fungus including C. albicans isolates, Trichophyton mentagrophytes, and other Gram-positive bacteria. Specific activity against periodontopathic bacteria, such as Porphyromonas gingivalis, Actinobacillus actinomycetemcomitans, Fusobacterium nucleatum, Streptococcus mutans, and Streptococcus sobrinus has also been observed [A32250, A32251, A32252, A32253, A32254].', 'Lipophilic monoterpene formulations of eucalyptus oil appear to be readily absorbed orally, with a primarily oxidative metabolism that might necessitate induction of the cytochrome P450 enzyme system and subsequent urinary excretion [A32236]. Gastrointestinal absorption of eucalyptus appears to be rapid and may be enhanced by the intake of lipids and milk. 1,8-cineole (which makes up to as much as 90% of most commonly used cineole-based eucalyptus oils) [A32227] has also been found in vitro and in animals to possess cytochrome P450 inducing activity [A32237, A32238, A32239].', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1805, 'Amipride 50mg 30 tab', 'اميبريد 50مجم 30 قرص', '111', '81', 'Psychiatric.antipsychotics', 'Amisulpride', 'Sigma > andalous pharma', 'Tab', 'أقراص', '50mg', '3', NULL, NULL, '6223004692659', NULL, 2364, '2024-08-01', 'Intravenous amisulpride is indicated in adults for the prevention of postoperative nausea and vomiting, either alone or in combination with an antiemetic of a different class. It is also indicated for the treatment of postoperative nausea and vomiting in patients who have received anti-emetic prophylaxis with an agent of a different class or have not received prophylaxis.[L32759]



Oral amisulpride is indicated for the treatment of acute and chronic schizophrenic disorders, characterized by positive symptoms with delusions, hallucinations, thought disorders, hostility and suspicious behavior; or primarily negative symptoms (deficit syndrome) with blunted affect, emotional and social withdrawal. Amisulpride also controls secondary negative symptoms in productive conditions as well as affective disorders such as depressive mood or retardation.[L32764]', 'Dopamine is an essential and critical neurotransmitter produced in the substantia nigra and ventral tegmental regions of the brain. Dopaminergic projection function in the nigrostriatal, mesolimbic, and mesocortical systems. Hyperactive dopamine transmission in the mesolimbic areas, or dopamine dysregulation in various major brain regions, is understood as the key driver of positive and negative symptoms of schizophrenia.[A189156] Many antipsychotic agents act as D2 receptor antagonists, as with amisulpride.[A6752] Amisulpride is a selective dopamine D2 and D3 receptor antagonist.[L32759] It has high preferential activity towards dopamine receptors in the limbic system rather than the striatum, leading to a lower risk of extrapyramidal side effects than other atypical antipsychotic agents.[A6754, A232269] At low doses, amisulpride reduces negative symptoms of schizophrenia by blocking pre-synaptic dopamine D2 and D3 receptors, increasing the levels of dopamine in the synaptic cleft and facilitating dopaminergic transmission.[A6754] At higher doses, amisulpride blocks postsynaptic receptors, inhibiting dopaminergic hyperactivity: this explains the drug improving positive symptoms.[A6751] Amisulpride also works as an antagonist at 5-HT<sub>7A</sub> receptors [A16931] and 5-HT<sub>2A</sub> receptors,[A6754] which may be related to its antidepressant effects.[A6755]



The chemoreceptor trigger zone (CTZ), also commonly known as the area postrema (AP), is an important brain region located within the dorsal surface of the medulla oblongata. CTZ is involved in emesis: it contains receptors, such as dopamine receptors, that are activated in response to emetic agents in the blood and relay information to the vomiting center, which is responsible for inducing the vomiting reflex.[A232374] Amisulpride is an antiemetic agent that works to limit signals that promote nausea and vomiting. Amisulpride binds to D2 and D3 receptors in the CTZ, leading to reduced dopaminergic signalling into the vomiting center.[L32759]



', 'Amisulpride is a selective dopamine D2 and D3 receptor antagonist with no affinity towards other dopamine receptor subtypes. Amisulpride is an atypical antipsychotic agent that works as an antagonist at dopamine receptors in the limbic system. Since it works preferentially in the limbic system, amisulpride is less likely to be associated with extrapyramidal adverse effects than other atypical antipsychotic agents. Amisulpride has no affinity for serotonin, alpha-adrenergic, H1-histamine, cholinergic, and sigma receptors. In clinical trials, amisulpride improved reduced secondary negative symptoms, affective symptoms, and psychomotor retardation in patients with acute exacerbation of schizophrenia. Notably, amisulpride has a differential target binding profile at different doses: at low doses, amisulpride selectively binds to presynaptic dopamine autoreceptors. At high doses, it preferentially binds to post-synaptic dopamine receptors.[A6753] This explains how amisulpride reduces negative symptoms at low doses and mediates antipsychotic effects at high doses.[L32764] One study alluded that the antinociceptive effects of amisulpride are mediated through opioid receptor acvitation and D2 receptor antagonism. [A6753, A6755] The actions of amisulpride at opioid receptors may explain its pro-convulsant properties.[A6755]



Amisulpride is also an antiemetic agent that prevents and alleviates postoperative nausea and vomiting. It primarily works by blocking dopamine signalling in the chemoreceptor trigger zone, which is a brain area that relays stimuli to the vomiting center.[L32764]



In clinical trials comprising Caucasian and Japanese subjects, amisulpride caused dose- and concentration-dependent prolongation of the QT interval; thus, intravenous infusion under a strict dosing regimen and close monitoring of patients with pre-existing cardiovascular conditions are recommended.[L32759] Amisulpride increases plasma prolactin levels, leading to an association with benign pituitary tumours such as prolactinoma.[L32764]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28367, 'Total forte 30 caps.', 'توتال فورت 30 كبسولة', '237', NULL, 'Omega 3', 'Omega 3', 'Hochster pharmaceutical industries', 'Cap', 'كبسولة', NULL, '3', NULL, 'total is a suspension with natural orange flavor rich in vitamin a vitamin d and omega 3 fatty acids vitamin a : protects children and young adults from vitamin a deficiency night blindness vitamin d : helps normal growth of bone and teeth omega 3 fatty a', NULL, NULL, 1282, '2023-03-27', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (29443, 'Icohaem 30 capsules', 'ايكوهيم 30 كبسولة', '97', NULL, 'Multi vitamins', 'Iron + zinc + copper + vitamin b12 + vitamin b6 + folic acid', 'Iconic pharmaceutical', 'Capsule', 'كبسولة', NULL, '3', NULL, NULL, NULL, NULL, 569, '2022-08-17', 'For treatment of patients with documented iron deficiency in whom oral administration is unsatisfactory or impossible. Also used to replenish body iron stores in Non-Dialysis Dependent-Chronic Kidney Disease (NDD-CKD) patients receiving or not receiving erythropoietin and in Hemodialysis Dependent (HDD-CKD) and Peritoneal Dialysis Dependent (PDD-CKD) - Chronic Kidney Disease patients receiving an erythropoietin.', 'After iron dextran is injected, the circulating iron dextran is removed from the plasma by cells of the reticuloendothelial system, which split the complex into its components of iron and dextran. The iron is immediately bound to the available protein moieties to form hemosiderin or ferritin, the physiological forms of iron, or to a lesser extent to transferrin. This iron which is subject to physiological control replenishes hemoglobin and depleted iron stores.', 'Iron dextran is a dark brown, slightly viscous sterile liquid complex of ferric hydroxide and dextran for intravenous or intramuscular use. It is for treatment of patients with documented iron deficiency in whom oral administration is unsatisfactory or impossible. Iron is essential to the formation of hemoglobin and to the function and formation of other heme and nonheme compounds. Untreated depletion of iron stores leads to iron-deficient erythropoiesis and, in turn, to iron deficiency anemia.', 'DrugBank', 0, 0, 0);
