INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25564, 'Quitcool xr 200mg 30 ext. rel. f.c. tabs.', 'كويتكول اكس ار 200مجم 30 اقراص', '264', '201', 'Psychiatric.antipsychotics', 'Quetiapine', 'Hikma pharma', 'Tab', 'أقراص', '200mg', '3', NULL, 'indication for the treatment of schizophrenia and related psychotic disorders. pharmacodynamics quetiapine is a psychotropic agent belonging to the chemical class of benzisoxazole derivatives and is indicated for the treatment of schizophrenia. quetiapine', NULL, NULL, 1064, '2025-02-09', 'Quetiapine is used in the symptomatic treatment of schizophrenia.  In addition, it may be used for the management of acute manic or mixed episodes in patients with bipolar I disorder, as a monotherapy or combined with other drugs. It may be used to manage depressive episodes in bipolar disorder. In addition to the above indications, quetiapine is used in combination with antidepressant drugs for the treatment of major depression.[L8546]



Some off-label uses for this drug include the management of post-traumatic stress disorder (PTSD), generalized anxiety disorder, and psychosis associated with Parkinson''s disease.[A185438,A185447,T685]

', 'Although the mechanism of action of quetiapine is not fully understood, several proposed mechanisms exist. In schizophrenia, its actions could occur from the antagonism of dopamine type 2 (D2) and serotonin 2A (5HT2A) receptors. In bipolar depression and major depression, quetiapine''s actions may be attributed to the binding of this drug or its metabolite to the norepinephrine transporter. Additional effects of quetiapine, including somnolence, orthostatic hypotension, and anticholinergic effects, may result from the antagonism of H1 receptors, adrenergic α1 receptors, and muscarinic M1 receptors, respectively.[A2189,A185438,L8546]', 'Quetiapine improves the positive and negative symptoms of schizophrenia and major depression by acting on various neurotransmitter receptors, such as the serotonin and dopamine receptors. In bipolar disorder, it improves both depressive and manic symptoms.[A2189,A185441,A185444]



**A note on suicidality in young patients and administration in the elderly**



Quetiapine can cause suicidal thinking or behavior in children and adolescents and should not be given to children under 10 years of age. It is important to monitor for suicidality if this drug is given to younger patients. In addition, this drug is not indicated for the treatment of psychosis related to dementia due to an increased death rate in elderly patients taking this drug.[L8546]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14359, 'Aranesp 500mcg 1 pref. syringe (n/a yet)', 'ارانيسب 500مكجم سرنجة معبأة للحقن', '7,500', NULL, 'Hematopoietic.erythropoiesis-stimulating agent (esa)', 'Darbepoetin alfa', 'Amgen > multipharma', 'Syringe', 'حقنة', '500mcg', '1', NULL, 'darbepoetin alfa is a synthetic form of erythropoietin. it stimulates erythropoiesis (increases red blood cell levels) and is used to treat anemia commonly associated with chronic renal failure and cancer chemotherapy.', NULL, NULL, 884, '2022-08-03', 'For the treatment of anemia (from renal transplants or certain HIV treatment)', 'Darbepoetin alfa stimulates erythropoiesis by the same mechanism as endogenous erythropoietin. Erythropoietin interacts with

progenitor stem cells to increase red cell production. Binding of erythropoietin to the erythropoietin receptor leads to receptor dimerization, which facilitates activation of JAK-STAT signaling pathways within the cytosol. Activated STAT (signal transducers and activators of transcription) proteins are then translocated to the nucleus where they serve as transcription factors which regulate the activation of specific genes involved in cell division or differentiation.', 'Darbepoetin alfa is used in the treatment of anemia. It is involved in the regulation of erythrocyte differentiation and the maintenance of a physiological level of circulating erythrocyte mass.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (12868, 'Etoricox-hexal 60 mg 30 f.c.tabs.', 'ايتوريكوكس هيكسال 60مجم 30 قرص', '195', NULL, 'Nsaid.selective cox-2 inhibitors', 'Etoricoxib', 'Hexal ag germany > novartis pharma scientific office', 'Tab', 'أقراص', '60 mg', '3', NULL, NULL, NULL, NULL, 1981, '2023-07-19', 'For the treatment of rheumatoid arthritis, osteoarthritis, ankylosing spondylitis, chronic low back pain, acute pain and gout.', 'Like any other COX-2 selective inhibitor Etoricoxib selectively inhibits isoform 2 of cyclo-oxigenase enzyme (COX-2), preventing production of prostaglandins (PGs) from arachidonic acid.', 'Etoricoxib is a COX-2 selective inhibitor (approximately 106 times more selective for COX-2 inhibition over COX-1).', 'DrugBank', 0, 0, 0);
